import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Sidebar from '../components/shared/SideBar/Sidebar';
import TeamHeader from '../components/shared/TeamHeader/TeamHeader';
import { fetchProjectById, fetchTasksBySprintId } from '../services/groupService';
import './css/Project.css';

const Project = () => {
  const { userId, classId, projectId, sprintId, tab } = useParams();
  const navigate = useNavigate();
  const [projectData, setProjectData] = useState(null);
  const [tasks, setTasks] = useState({ teamTasks: [], myTasks: [] });
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  // Lấy thông tin project
  useEffect(() => {
    const loadProjectData = async () => {
      setLoading(true);
      try {
        if (!projectId || isNaN(projectId)) {
          throw new Error('projectId không hợp lệ');
        }
        const project = await fetchProjectById(projectId);
        if (!project || !project.project_id) {
          throw new Error('Không tìm thấy dự án');
        }
        setProjectData(project);

        // Nếu không có sprintId hoặc tab trong URL, chọn sprint đang hoạt động và tab "Team task"
        if (!sprintId || !tab) {
          if (project.sprints && project.sprints.length > 0) {
            const currentDate = new Date();
            const activeSprint = project.sprints.find((sprint) => {
              const startDate = new Date(sprint.start_date);
              const endDate = new Date(sprint.end_date);
              return startDate <= currentDate && currentDate <= endDate;
            });

            const selectedSprint = activeSprint || project.sprints[project.sprints.length - 1];
            navigate(
              `/userID/${userId}/classes/${classId}/projects/${projectId}/sprints/${selectedSprint.sprint_id}/team-task`,
              { replace: true }
            );
          } else {
            navigate(`/userID/${userId}/classes/${classId}/projects/${projectId}/introduce`, { replace: true });
          }
        }
      } catch (err) {
        setError('Không thể tải dữ liệu dự án. Vui lòng thử lại sau.');
        console.error('Error in loadProjectData:', err.message);
      } finally {
        setLoading(false);
      }
    };
    loadProjectData();
  }, [projectId, userId, classId, navigate]);

  // Lấy danh sách tasks khi sprintId thay đổi
  useEffect(() => {
    const loadTasks = async () => {
      if (!sprintId) return;
      setLoading(true);
      try {
        const taskData = await fetchTasksBySprintId(sprintId, userId);
        setTasks(taskData);
      } catch (err) {
        setError('Không thể tải dữ liệu nhiệm vụ. Vui lòng thử lại sau.');
        console.error('Error in loadTasks:', err.message);
      } finally {
        setLoading(false);
      }
    };
    loadTasks();
  }, [sprintId, userId]);

  const handleSprintChange = (sprint) => {
    const currentTab = activeTab.toLowerCase().replace(' ', '-');
    navigate(`/userID/${userId}/classes/${classId}/projects/${projectId}/sprints/${sprint.sprint_id}/${currentTab}`);
  };

  const handleTabChange = (tabName) => {
    const tabPath = tabName.toLowerCase().replace(' ', '-');
    let selectedSprintId = sprintId;
    if (!selectedSprintId && projectData?.sprints?.length > 0) {
      const currentDate = new Date();
      const activeSprint = projectData.sprints.find((sprint) => {
        const startDate = new Date(sprint.start_date);
        const endDate = new Date(sprint.end_date);
        return startDate <= currentDate && currentDate <= endDate;
      });
      selectedSprintId = (activeSprint || projectData.sprints[projectData.sprints.length - 1]).sprint_id;
    }

    if (selectedSprintId) {
      navigate(`/userID/${userId}/classes/${classId}/projects/${projectId}/sprints/${selectedSprintId}/${tabPath}`);
    } else {
      navigate(`/userID/${userId}/classes/${classId}/projects/${projectId}/${tabPath}`);
    }
  };

  const activeTab = tab?.replace('-', ' ') || (sprintId ? 'Team task' : 'Introduce');

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  if (loading || !projectData) {
    return <div className="loading">Đang tải dữ liệu...</div>;
  }

  return (
    <div className="project-page">
      <Sidebar />
      <div className="content-container">
        <TeamHeader
          className={projectData.class_name || 'Tên lớp'}
          classCode={projectData.class_code || '100001'}
          teamName={projectData.group_name || 'Tên nhóm'}
          projectName={projectData.project_name || 'Tên dự án'}
          members={projectData.members || []}
          activeTab={activeTab}
          sprints={projectData.sprints || []}
          selectedSprintId={sprintId}
          onSprintChange={handleSprintChange}
          onTabChange={handleTabChange}
        />
        <div className="project-content">
          {activeTab === 'Team task' && sprintId && (
            <>
              <h3>Team Tasks (Sprint {sprintId})</h3>
              {tasks.teamTasks?.length > 0 ? (
                tasks.teamTasks.map((task) => (
                  <div key={task.task_id} style={{ marginBottom: '10px', padding: '10px', border: '1px solid #e0e0e0', borderRadius: '5px' }}>
                    <p><strong>{task.title}</strong></p>
                    <p>Assignee: {task.assignee_name || 'Chưa giao'}</p>
                    <p>Status: {task.status}</p>
                    {task.due_date && <p>Due Date: {new Date(task.due_date).toLocaleDateString()}</p>}
                  </div>
                ))
              ) : (
                <p>Không có nhiệm vụ nào cho sprint này.</p>
              )}
            </>
          )}
          {activeTab === 'My task' && sprintId && (
            <>
              <h3>My Tasks (Sprint {sprintId})</h3>
              {tasks.myTasks?.length > 0 ? (
                tasks.myTasks.map((task) => (
                  <div key={task.task_id} style={{ marginBottom: '10px', padding: '10px', border: '1px solid #e0e0e0', borderRadius: '5px' }}>
                    <p><strong>{task.title}</strong></p>
                    <p>Assignee: {task.assignee_name || 'Chưa giao'}</p>
                    <p>Status: {task.status}</p>
                    {task.due_date && <p>Due Date: {new Date(task.due_date).toLocaleDateString()}</p>}
                  </div>
                ))
              ) : (
                <p>Không có nhiệm vụ nào cho bạn trong sprint này.</p>
              )}
            </>
          )}
          {activeTab === 'Introduce' && (
            <>
              <h3>Introduce</h3>
              <p>Đây là trang giới thiệu của dự án {projectData.project_name}.</p>
            </>
          )}
          {activeTab === 'Dashboard' && (
            <>
              <h3>Dashboard</h3>
              <p>Đây là dashboard của dự án {projectData.project_name}.</p>
            </>
          )}
          {activeTab === 'Roadmap' && (
            <>
              <h3>Roadmap</h3>
              <p>Đây là roadmap của dự án {projectData.project_name}.</p>
            </>
          )}
          {activeTab === 'Rate' && (
            <>
              <h3>Rate</h3>
              <p>Đây là trang đánh giá của dự án {projectData.project_name}.</p>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Project;
