import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Sidebar from '../components/shared/SideBar/Sidebar';
import TeamHeader from '../components/shared/TeamHeader/TeamHeader.jsx';
import { fetchProjectById, fetchTasksBySprintId } from '../services/groupService';
import './css/Project.css';

const Project = () => {
  const { userId, classId, projectId, sprintId } = useParams();
  const navigate = useNavigate();
  const [projectData, setProjectData] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadData = async () => {
      try {
        const project = await fetchProjectById(projectId);
        if (project && project.project_id) {
          console.log('Project Data:', project);
          console.log('Members:', project.members);
          setProjectData(project);
        } else {
          setError('Không tìm thấy dự án');
        }

        if (sprintId) {
          const taskData = await fetchTasksBySprintId(projectId, sprintId);
          setTasks(taskData);
        }
      } catch (err) {
        setError('Không thể tải dữ liệu. Vui lòng thử lại sau.');
        console.error('Error in loadData:', err.message);
      }
    };
    loadData();
  }, [projectId, sprintId]);

  const handleSprintChange = (sprint) => {
    navigate(`/userID/${userId}/classes/${classId}/projects/${projectId}/sprints/${sprint.sprint_id}`);
  };

  const handleTabChange = (tabName) => {
    const tabPath = tabName === 'Team task' ? `sprints/${projectData.sprints[projectData.sprints.length - 1]?.sprint_id || ''}` : tabName.toLowerCase().replace(' ', '-');
    navigate(`/userID/${userId}/classes/${classId}/projects/${projectId}/${tabPath}`);
  };

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="project-page">
      <Sidebar />
      <div className="content-container">
        {projectData ? (
          <>
            <TeamHeader
              teamName={projectData.group_name || 'Name team'}
              members={projectData.members || []}
              activeTab={sprintId ? 'Team task' : 'Introduce'}
              sprints={projectData.sprints || []}
              onSprintChange={handleSprintChange}
              onTabChange={handleTabChange}
            />
            <div className="project-content">
              {sprintId ? (
                <>
                  <h3>Tasks</h3>
                  {tasks.length > 0 ? (
                    tasks.map((task, index) => (
                      <div key={index}>
                        <p>{task.task_name}</p>
                      </div>
                    ))
                  ) : (
                    <p>Không có nhiệm vụ nào để hiển thị.</p>
                  )}
                </>
              ) : (
                <>
                  <h3>Introduce</h3>
                  <p>Project: {projectData.project_name}</p>
                  <p>Group: {projectData.group_name}</p>
                  <p>Members: {projectData.members?.length || 0}</p>
                </>
              )}
            </div>
          </>
        ) : (
          <p>Đang tải dữ liệu...</p>
        )}
      </div>
    </div>
  );
};

export default Project;
