import React from 'react';
import './ClassCard.css';

const ClassCard = ({ className, groupName, projectName, memberCount, avatarNumber, avatarColor, members }) => {
  return (
    <div className="classcard-container">
      {/* Avatar và số nhóm */}
      <div className="classcard-avatar">
        <div className="classcard-avatar-bg" style={{ backgroundColor: avatarColor }}></div>
        <div className="classcard-avatar-number">{avatarNumber}</div>
      </div>

      {/* Tên lớp và mô tả */}
      <div className="classcard-name-description">
        <div className="classcard-name">
          <div className="classcard-title">{className}</div>
        </div>
        <div className="classcard-description">
          <div className="classcard-description-text">{groupName}: {projectName}</div>
        </div>
      </div>

      {/* Danh sách thành viên */}
      <div className="classcard-members">
        <div className="classcard-members-list">
          {members.slice(0, 5).map((member, index) => (
            <div key={index} className="classcard-member">
              <img
                className="classcard-member-img"
                src={`/assets/images/avatars/${member.avatar}`}
                alt={`Member ${index + 1}`}
                onError={(e) => {
                  e.target.src = 'https://via.placeholder.com/22?text=M'; // Hình mặc định nếu không tìm thấy
                }}
              />
            </div>
          ))}
        </div>
        <div className="classcard-members-count">{memberCount} members</div>
      </div>

      {/* Icon More */}
      <img
        className="classcard-more-icon"
        src="https://via.placeholder.com/24?text=More"
        alt="More options"
      />
    </div>
  );
};

export default ClassCard;
