import React from "react";
import "../../assets/styles/global.css";
import "./KanbanView.css";
import Sidebar from "../Sidebar/Sidebar";
const KanbanView = () => {
  const reportData = [
    { id: 1, name: "To-Do", status: "to-do", count: 2 },
    { id: 2, name: "In-progress", status: "in-progress", count: 3 },
    { id: 3, name: "Completed", status: "completed", count: 2 },
  ];

  const tasks = [
    {
      id: 1,
      title: "Name task",
      date: "6 July, 2020",
      time: "18:00",
      tags: ["SQL", "Backend"],
      avatar: "https://i.pravatar.cc/24?img=1",
      comments: 3,
      subTasks: 5,
      status: "to-do",
    },
    {
      id: 2,
      title: "Name task",
      date: "6 July, 2020",
      time: "18:00",
      tags: ["SQL", "Backend"],
      avatar: "https://i.pravatar.cc/24?img=2",
      comments: 3,
      subTasks: 5,
      status: "in-progress",
    },
    {
      id: 3,
      title: "Name task",
      date: "5 July, 2022",
      time: "18:00",
      tags: ["SQL", "Backend"],
      avatar: "https://i.pravatar.cc/24?img=3",
      comments: 3,
      subTasks: 3,
      status: "completed",
    },
  ];

  const getStatusClass = (status) => {
    switch (status) {
      case "to-do":
        return "report-status-to-do";
      case "in-progress":
        return "report-status-in-progress";
      case "completed":
        return "report-status-completed";
      default:
        return "";
    }
  };

  return (
    <div className="report-wrapper">
      <div Sidebar/>>
      <div className="report-header">
        <h1 className="h1">Team Task</h1>
        <select className="report-select">
          <option>Sprint 4</option>
          <option>Sprint 3</option>
        </select>
      </div>

      <h4 className="report-subtitle h4">Tổng quan tiến độ theo trạng thái</h4>

      <div className="report-grid">
        {reportData.map((group) => (
          <div key={group.id}>
            <h3 className="h3">
              {group.name} <span className="blueForActive">({group.count})</span>
            </h3>
            {tasks
              .filter((task) => task.status === group.status)
              .map((task) => (
                <div
                  key={task.id}
                  className={`report-card ${getStatusClass(task.status)}`}
                >
                  <div className="report-card-header">
                    <h4 className="body_1">{task.title}</h4>
                    <img
                      src={task.avatar}
                      alt="avatar"
                      className="task-avatar"
                    />
                  </div>
                  <div className="body_2" style={{ color: "#999" }}>
                    {task.date} • {task.time}
                  </div>
                  <div>
                    {task.tags.map((tag) => (
                      <span
                        key={tag}
                        className={`tag ${tag.toLowerCase()}`}
                      >
                        {tag}
                      </span>
                    ))}
                  </div>
                  <div className="task-actions body_2">
                    <span>{task.subTasks} 📋</span>
                    <span>{task.comments} 💬</span>
                  </div>
                </div>
              ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default KanbanView;
