import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Sidebar from '../components/shared/SideBar/Sidebar';
import { fetchClassMembers } from '../services/instructorGroupService';
import './css/ClassMembersPage.css';

const ClassMembersPage = () => {
  const { userId, classId } = useParams();
  const [members, setMembers] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const loadData = async () => {
      setLoading(true);
      try {
        if (!userId || isNaN(userId) || !classId || isNaN(classId)) {
          throw new Error('userId hoặc classId không hợp lệ');
        }
        const memberData = await fetchClassMembers(classId);
        console.log('Dữ liệu thành viên từ API:', memberData);
        if (memberData && Array.isArray(memberData)) {
          setMembers(memberData);
        } else {
          setMembers([]);
        }
      } catch (err) {
        setError('Không thể tải danh sách thành viên. Vui lòng thử lại sau.');
        console.error('Error in loadData:', err.message);
      } finally {
        setLoading(false);
      }
    };
    loadData();
  }, [userId, classId]);

  // Nhóm sinh viên theo group_id
  const groupedMembers = members.reduce((acc, member) => {
    const groupId = member.group_id || 'no-group';
    if (!acc[groupId]) {
      acc[groupId] = [];
    }
    acc[groupId].push(member);
    return acc;
  }, {});

  // Lấy danh sách nhóm từ backend (giả định đã có groupName trong member data)
  const groups = Object.keys(groupedMembers)
    .filter(key => key !== 'no-group')
    .map(key => ({
      groupId: key,
      groupName: groupedMembers[key][0]?.groupName || `Nhóm ${key}`,
      members: groupedMembers[key]
    }));

  const noGroupMembers = groupedMembers['no-group'] || [];

  if (error) {
    return (
      <div className="class-members-page">
        <Sidebar userId={userId} />
        <div className="class-members-content">
          <div className="error-message">{error}</div>
        </div>
      </div>
    );
  }

  return (
    <div className="class-members-page">
      <Sidebar userId={userId} />
      <div className="class-members-content">
        <div className="class-members-header">
          <h2>Danh sách sinh viên</h2>
          <div className="class-nav">
            <a href={`/userID/${userId}/instructor/classes/${classId}`}>Groups</a>
            <a href={`/userID/${userId}/instructor/classes/${classId}/settings`}>Settings</a>
          </div>
        </div>
        <div className="class-members-main">
          <div className="members-list">
            {loading ? (
              <p>Đang tải...</p>
            ) : members.length > 0 ? (
              <>
                {groups.map((group, index) => (
                  <div key={group.groupId} className="group-section">
                    <h3 className="group-title">{group.groupName}</h3>
                    <table className="members-table">
                      <thead>
                        <tr>
                          <th>Avatar</th>
                          <th>Tên</th>
                          <th>Email</th>
                          <th>Vai trò</th>
                        </tr>
                      </thead>
                      <tbody>
                        {group.members.map((member, memberIndex) => (
                          <tr key={memberIndex}>
                            <td>
                              <img
                                src={member.avatar || '/uploads/default.jpg'}
                                alt={`Avatar ${member.username}`}
                                className="member-avatar"
                              />
                            </td>
                            <td>{member.username}</td>
                            <td>{member.email}</td>
                            <td>{member.isPM ? 'PM' : 'Thành viên'}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                    {index < groups.length - 1 && <hr className="group-divider" />}
                  </div>
                ))}
                {noGroupMembers.length > 0 && (
                  <div className="group-section">
                    <h3 className="group-title">Chưa có nhóm</h3>
                    <table className="members-table">
                      <thead>
                        <tr>
                          <th>Avatar</th>
                          <th>Tên</th>
                          <th>Email</th>
                          <th>Vai trò</th>
                        </tr>
                      </thead>
                      <tbody>
                        {noGroupMembers.map((member, index) => (
                          <tr key={index}>
                            <td>
                              <img
                                src={member.avatar || '/uploads/default.jpg'}
                                alt={`Avatar ${member.username}`}
                                className="member-avatar"
                              />
                            </td>
                            <td>{member.username}</td>
                            <td>{member.email}</td>
                            <td>{member.isPM ? 'PM' : 'Thành viên'}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                )}
              </>
            ) : (
              <p>Không có sinh viên nào trong lớp học này.</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ClassMembersPage;
