import React, { useState } from "react";
// import "../../assets/styles/global.css";
import "./KanbanView.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCheckCircle as fasCheckCircle } from "@fortawesome/free-solid-svg-icons";
import { faCircle as farCircle } from "@fortawesome/free-regular-svg-icons";
import { faClipboard } from "@fortawesome/free-solid-svg-icons";
import { faComment } from "@fortawesome/free-solid-svg-icons";

export const KanbanView = () => {
  const [tasks, setTasks] = useState([
    {
      id: 1,
      title: "Name task",
      date: "6 July, 2020",
      time: "18:00",
      tags: ["SQL", "Backend"],
      avatar: "https://i.pravatar.cc/24?img=1",
      comments: 3,
      subTasks: [
        { id: 1, text: "Play music", completed: true },
        { id: 2, text: "Play music", completed: false },
      ],
      status: "to-do",
    },
    {
      id: 2,
      title: "Name task",
      date: "6 July, 2020",
      time: "18:00",
      tags: ["SQL", "Backend"],
      avatar: "https://i.pravatar.cc/24?img=2",
      comments: 3,
      subTasks: [
        { id: 1, text: "Play music", completed: true },
        { id: 2, text: "Play music", completed: false },
      ],
      status: "to-do",
    },
    {
      id: 3,
      title: "Name task",
      date: "6 July, 2020",
      time: "18:00",
      tags: ["SQL", "Backend"],
      avatar: "https://i.pravatar.cc/24?img=3",
      comments: 3,
      subTasks: [
        { id: 1, text: "Play music", completed: true },
        { id: 2, text: "Play music", completed: false },
      ],
      status: "in-progress",
    },
    {
      id: 4,
      title: "Name task",
      date: "6 July, 2020",
      time: "18:00",
      tags: ["SQL", "Backend"],
      avatar: "https://i.pravatar.cc/24?img=4",
      comments: 3,
      subTasks: [
        { id: 1, text: "Play music", completed: true },
        { id: 2, text: "Play music", completed: false },
      ],
      status: "in-progress",
    },
    {
      id: 5,
      title: "Name task",
      date: "18:00 5 July, 2022",
      time: "",
      tags: ["SQL", "Backend"],
      avatar: "https://i.pravatar.cc/24?img=5",
      comments: 3,
      subTasks: [
        { id: 1, text: "Play music", completed: true },
        { id: 2, text: "Play music", completed: true },
      ],
      status: "completed",
    },
    {
      id: 6,
      title: "Name task",
      date: "18:00 5 July, 2022",
      time: "",
      tags: ["SQL", "Backend"],
      avatar: "https://i.pravatar.cc/24?img=6",
      comments: 3,
      subTasks: [
        { id: 1, text: "Play music", completed: true },
        { id: 2, text: "Play music", completed: true },
      ],
      status: "completed",
      overdue: true,
    },
  ]);

  const reportData = [
    { id: 1, name: "To-Do", status: "to-do", count: 2 },
    { id: 2, name: "In-progress", status: "in-progress", count: 2 },
    { id: 3, name: "Completed", status: "completed", count: 2 },
  ];

  const getStatusClass = (status, overdue = false) => {
    if (overdue) return "report-status-overdue";
    switch (status) {
      case "to-do":
        return "report-status-to-do";
      case "in-progress":
        return "report-status-in-progress";
      case "completed":
        return "report-status-completed";
      default:
        return "";
    }
  };

  const getCircleClass = (status) => {
    switch (status) {
      case "to-do":
        return "circle-to-do";
      case "in-progress":
        return "circle-in-progress";
      case "completed":
        return "circle-completed";
      default:
        return "";
    }
  };

  const toggleSubTask = (taskId, subTaskId) => {
    setTasks((prevTasks) =>
      prevTasks.map((task) =>
        task.id === taskId
          ? {
              ...task,
              subTasks: task.subTasks.map((subTask) =>
                subTask.id === subTaskId
                  ? { ...subTask, completed: !subTask.completed }
                  : subTask
              ),
            }
          : task
      )
    );
  };

  return (
    <div className="report-grid">
      {reportData.map((group) => (
        <div key={group.id} className="report-column">
          <div className="column-header">
            <h3 className="h3">
              {group.name}{" "}
              <span className="count-text">
                ({group.count.toString().padStart(2, "0")})
              </span>
            </h3>
            <span className={`status-circle ${getCircleClass(group.status)}`}></span>
          </div>
          {tasks
            .filter((task) => task.status === group.status)
            .map((task) => (
              <div
                key={task.id}
                className={`report-card ${getStatusClass(task.status, task.overdue)}`}
              >
                <div className="report-card-header">
                  <div className="header-content">
                    <h4 className="body_1">{task.title}</h4>
                    <div className="task-meta">
                      {task.date} {task.time}
                    </div>
                  </div>
                </div>
                <div className="task-tags">
                  {task.tags.map((tag) => (
                    <span key={tag} className={`tag ${tag.toLowerCase()}`}>
                      {tag}
                    </span>
                  ))}
                </div>
                <div className="task-info">
                  <img
                    src={task.avatar}
                    alt="avatar"
                    className="task-avatar"
                  />
                  <div className="task-actions body_2">
                    <span className="task-action-item">
                      {task.subTasks.length} <FontAwesomeIcon icon={faClipboard} />
                    </span>
                    <span className="task-action-item">
                      {task.comments} <FontAwesomeIcon icon={faComment} />
                    </span>
                  </div>
                </div>
                <div className="task-subtasks">
                  {task.subTasks.map((subTask) => (
                    <div key={subTask.id} className="subtask">
                      <span className="subtask-count">{subTask.id}.</span>
                      <span className="subtask-text">{subTask.text}</span>
                      <span
                        className={`subtask-status ${subTask.completed ? "completed" : ""}`}
                        onClick={() => toggleSubTask(task.id, subTask.id)}
                      >
                        <FontAwesomeIcon
                          icon={subTask.completed ? fasCheckCircle : farCircle}
                        />
                      </span>
                    </div>
                  ))}
                </div>
              </div>
            ))}
        </div>
      ))}
    </div>
  );
};
export default KanbanView;
