import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Sidebar.css';
import axios from 'axios';

const Sidebar = ({ userId }) => {
  const [isOpen, setIsOpen] = useState(true);
  const [selectedItem, setSelectedItem] = useState('Teams');
  const [user, setUser] = useState({ username: 'Loading...', avatar: '/assets/images/avatar-default.svg' });
  const navigate = useNavigate();

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  const handleItemClick = (item) => {
    setSelectedItem(item);
  };

  const handleProfileClick = () => {
    if (userId) {
      navigate(`/userId/${userId}/profile`);
    } else {
      console.warn('userId không tồn tại để điều hướng đến profile');
    }
  };

  // Lấy thông tin người dùng từ API khi userId thay đổi
  useEffect(() => {
    if (userId && !isNaN(userId)) {
      const fetchUser = async () => {
        try {
          const response = await axios.get(`http://localhost:5000/api/users/${userId}`);
          console.log('API response:', response.data); // Debug
          setUser({
            username: response.data.username || 'Unknown User',
            avatar: response.data.avatar || '/assets/images/avatar-default.svg'
          });
        } catch (error) {
          console.error('Lỗi khi lấy thông tin người dùng:', error);
          setUser({ username: 'Error Loading', avatar: '/assets/images/avatar-default.svg' });
        }
      };
      fetchUser();
    } else {
      console.warn('userId không hợp lệ:', userId);
    }
  }, [userId]);

  return (
    <div className="sidebar flex">
      {/* Sidebar */}
      <div
        className={`bg-white h-screen border-r transition-all ${isOpen ? 'w-64' : 'w-0'} overflow-hidden`}
      >
        <div className="p-4">
          {/* Logo and Toggle Button */}
          <div className="flex items-center justify-between">
            <img src="/assets/images/worktrace-logo.svg" alt="WorkTrace Logo" className="logo" />
            <button
              onClick={toggleSidebar}
              className="open_close_sidebar"
            >
              <i className={isOpen ? 'fas fa-chevron-left' : 'fas fa-chevron-right'}></i>
            </button>
          </div>

          {/* User Info */}
          <div 
            className="user-ava-name flex items-center mt-6 cursor-pointer"
            onClick={handleProfileClick}
          >
            <img
              src={user.avatar}
              alt="User Avatar"
              className="avatar mr-3"
              onError={(e) => { e.target.src = '/assets/images/avatar-default.svg'; }} // Fallback nếu ảnh lỗi
            />
            <span className="user-name text-gray-700">{user.username}</span>
          </div>

          <div className="line"></div>

          {/* Menu Items */}
          <div className="sidebar-content mt-6">
            <a
              href="#"
              className={`sidebar-item flex items-center p-2 text-gray-700 rounded ${selectedItem === 'Teams' ? 'bg-blue-100' : ''}`}
              onClick={() => handleItemClick('Teams')}
            >
              <i className="icon mr-3 fa-solid fa-people-group"></i>
              <p>Teams</p>
            </a>
            <a
              href="#"
              className={`sidebar-item flex items-center p-2 text-gray-700 rounded ${selectedItem === 'Thông báo' ? 'bg-blue-100' : ''}`}
              onClick={() => handleItemClick('Thông báo')}
            >
              <i className="icon mr-3 fa-solid fa-bell"></i>
              <p>Thông báo</p>
            </a>
          </div>

          {/* Logout */}
          {isOpen && (
            <div className="logout absolute bottom-4 left-4">
              <a
                href="#"
                className="flex items-center p-2 text-gray-700 rounded"
                onClick={() => navigate('/logout')}
              >
                <i className="icon mr-3 fa-solid fa-right-from-bracket"></i>
                <p>Log out</p>
              </a>
            </div>
          )}
        </div>
      </div>

      {/* Toggle Button for when sidebar is closed */}
      {!isOpen && (
        <button
          onClick={toggleSidebar}
          className="open_close_sidebar fixed top-4 left-4 z-10"
        >
          <i className="fa-solid fa-chevron-right"></i>
        </button>
      )}
    </div>
  );
};

export default Sidebar;
