import React, { useState, useEffect } from 'react';
import avatarMap from '../../../utils/avatarMap';
import placeholderMember from '../../../assets/images/placeholders/placeholder-member.jpg';
import { fetchTeamsByUserId } from '../../../services/groupService';
import './TeamHeader.css';

const TeamHeader = ({ teamName, members, activeTab, sprints, onSprintChange, onTabChange, userId, onTeamChange }) => {
  const tabs = ['Introduce', 'Dashboard', 'Team task', 'My task', 'Roadmap', 'Rate'];
  const [teams, setTeams] = useState([]); // Danh sách nhóm từ backend
  const [selectedTeam, setSelectedTeam] = useState(teamName); // Nhóm hiện tại
  const [loadingTeams, setLoadingTeams] = useState(false);
  const [errorTeams, setErrorTeams] = useState(null);

  // Lấy danh sách nhóm từ backend khi component được mount
  useEffect(() => {
    const fetchTeams = async () => {
      setLoadingTeams(true);
      try {
        const data = await fetchTeamsByUserId(userId);
        setTeams(data);
        // Nếu teamName hiện tại nằm trong danh sách nhóm, chọn nó làm mặc định
        const currentTeam = data.find((team) => team.teamName === teamName);
        if (currentTeam) {
          setSelectedTeam(currentTeam.teamName);
        } else if (data.length > 0) {
          setSelectedTeam(data[0].teamName);
          onTeamChange(data[0]); // Gọi callback để cập nhật nhóm
        }
      } catch (err) {
        setErrorTeams('Không thể tải danh sách nhóm.');
        console.error('Error fetching teams:', err.message);
      } finally {
        setLoadingTeams(false);
      }
    };
    if (userId) {
      fetchTeams();
    }
  }, [userId, teamName, onTeamChange]);

  // Xử lý khi người dùng chọn nhóm
  const handleTeamChange = (event) => {
    const teamId = event.target.value;
    const team = teams.find((t) => t.teamId === teamId);
    if (team) {
      setSelectedTeam(team.teamName);
      onTeamChange(team); // Gọi callback để thông báo nhóm mới
    }
  };

  return (
    <div className="team-header">
      {/* Top bar: Team name and member avatars */}
      <div className="team-header-top">
        <div className="team-name">
          <span>{selectedTeam || teamName}</span>
          {loadingTeams ? (
            <span className="loading-text">Đang tải...</span>
          ) : errorTeams ? (
            <span className="error-text">{errorTeams}</span>
          ) : teams.length > 0 ? (
            <select className="team-select" value={teams.find((t) => t.teamName === selectedTeam)?.teamId || ''} onChange={handleTeamChange}>
              <option value="" disabled>Change team</option>
              {teams.map((team) => (
                <option key={team.teamId} value={team.teamId}>
                  {team.teamName}
                </option>
              ))}
            </select>
          ) : (
            <span className="no-teams-text">Không có nhóm</span>
          )}
          <select className="project-select">
            <option>Change project</option>
          </select>
        </div>
        <div className="team-members">
          {members && members.length > 0 ? (
            members.slice(0, 3).map((member, index) => (
              <div key={index} className="team-member">
                <img
                  src={avatarMap[member.avatar] || placeholderMember}
                  alt={`Member ${index + 1}`}
                  className="member-avatar"
                  onError={(e) => {
                    e.target.src = placeholderMember;
                  }}
                />
              </div>
            ))
          ) : (
            <p>No members</p>
          )}
          {members.length > 3 && (
            <div className="more-members">+{members.length - 3}</div>
          )}
        </div>
      </div>

      {/* Bottom bar: Tabs and Filter/Search */}
      <div className="team-header-tabs">
        <div className="sprint-select">
          <select
            onChange={(e) => {
              const sprint = sprints.find((s) => s.sprint_id === e.target.value);
              if (sprint) onSprintChange(sprint);
            }}
          >
            {sprints.map((sprint, index) => (
              <option key={index} value={sprint.sprint_id}>
                Sprint {index + 1}
              </option>
            ))}
          </select>
        </div>
        <div className="tabs">
          {tabs.map((tab, index) => (
            <button
              key={index}
              className={`tab ${activeTab === tab ? 'active' : ''}`}
              onClick={() => onTabChange(tab)}
            >
              {tab === 'Introduce' && <span className="icon introduce-icon"></span>}
              {tab === 'Dashboard' && <span className="icon dashboard-icon"></span>}
              {tab === 'Team task' && <span className="icon team-task-icon"></span>}
              {tab === 'My task' && <span className="icon my-task-icon"></span>}
              {tab === 'Roadmap' && <span className="icon roadmap-icon"></span>}
              {tab === 'Rate' && <span className="icon rate-icon"></span>}
              {tab}
            </button>
          ))}
        </div>
        <div className="actions">
          <button className="filter-btn">Filter</button>
          <div className="search-bar">
            <input type="text" placeholder="Search" />
            <span className="search-icon"></span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TeamHeader;
