import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Sidebar from '../components/shared/SideBar/Sidebar';
import TeamHeader from '../components/shared/TeamHeader/TeamHeader';
import ClassCard from '../components/shared/ClassCard/ClassCard';
import { fetchGroupsByUserId } from '../services/groupService';
import './css/GroupPage.css';

const GroupPage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const [groups, setGroups] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [selectedTeam, setSelectedTeam] = useState(null);

  // Hàm để lấy danh sách ClassCard
  const loadGroups = async (team) => {
    setLoading(true);
    try {
      if (!userId || isNaN(userId)) {
        throw new Error('userId không hợp lệ');
      }
      const data = await fetchGroupsByUserId(userId, team?.teamId);
      setGroups(data);
      setSelectedTeam(team);
    } catch (err) {
      setError('Không thể tải dữ liệu. Vui lòng thử lại sau.');
      console.error('Error in loadData:', err.message);
    } finally {
      setLoading(false);
    }
  };

  // Tải dữ liệu ban đầu
  useEffect(() => {
    loadGroups(null); // Ban đầu không chọn team cụ thể
  }, [userId]);

  // Xử lý khi team thay đổi
  const handleTeamChange = (team) => {
    loadGroups(team);
  };

  const handleClassCardClick = (classId, projectId) => {
    if (!classId || !projectId) {
      console.error('classId hoặc projectId không hợp lệ');
      return;
    }
    navigate(`/userID/${userId}/classes/${classId}/projects/${projectId}/introduce`);
  };

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="group-page">
      <Sidebar />
      <div className="content-container">
        <TeamHeader
          userId={userId}
          teamName={selectedTeam?.teamName || 'Classes'}
          members={[]}
          activeTab="Introduce"
          sprints={[]}
          onSprintChange={() => {}}
          onTabChange={() => {}}
          onTeamChange={handleTeamChange}
        />
        <div className="class-card-grid">
          {loading ? (
            <p>Đang tải...</p>
          ) : groups.length > 0 ? (
            groups.map((group, index) => (
              <ClassCard
                key={index}
                classId={group.classId}
                className={group.className}
                groupName={group.group_name}
                projectName={group.projectName}
                projectId={group.projectId}
                memberCount={group.memberCount}
                avatarNumber={group.avatarNumber}
                avatarColor={group.avatarColor}
                members={group.members}
                onClick={() => handleClassCardClick(group.classId, group.projectId)}
              />
            ))
          ) : (
            <p>Không có nhóm nào để hiển thị.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default GroupPage;
