// frontend/src/components/TeamHeader/TeamHeader.js
import React from "react";
import "./TeamHeader.css";

const TeamHeader = ({
  className,
  classCode,
  teamName,
  projectName,
  members,
  activeTab,
  sprints,
  selectedSprintId,
  onSprintChange,
  onTabChange,
  onUserChange, // Thêm prop để truyền user_id được chọn
}) => {
  const handleUserChange = (e) => {
    const userId = parseInt(e.target.value);
    onUserChange(userId === 0 ? null : userId); // Nếu chọn "All Users", truyền null
  };

  return (
    <div className="team-header">
      <div className="header-left">
        <h1>{className}</h1>
        <span className="class-code">{classCode}</span>
        <span className="team-name">{teamName}</span>
        <span className="project-name">{projectName}</span>
      </div>
      <div className="header-right">
        <div className="tabs">
          <button
            className={activeTab === "Team task" ? "active" : ""}
            onClick={() => onTabChange("Team task")}
          >
            Team task
          </button>
          <button
            className={activeTab === "My task" ? "active" : ""}
            onClick={() => onTabChange("My task")}
          >
            My task
          </button>
        </div>
        <div className="sprint-selector">
          <select
            value={selectedSprintId || ""}
            onChange={(e) => {
              const sprint = sprints.find(s => s.sprint_id === parseInt(e.target.value));
              onSprintChange(sprint);
            }}
          >
            {sprints.map((sprint) => (
              <option key={sprint.sprint_id} value={sprint.sprint_id}>
                {sprint.sprint_name}
              </option>
            ))}
          </select>
        </div>
        {/* Thêm dropdown lọc user */}
        <div className="user-selector">
          <select onChange={handleUserChange}>
            <option value="0">All Users</option>
            {members.map((member) => (
              <option key={member.user_id} value={member.user_id}>
                {member.username}
              </option>
            ))}
          </select>
        </div>
      </div>
    </div>
  );
};

export default TeamHeader;
