import React from 'react';
import './GroupInClass.css';

const GroupInClass = ({ group, userId, classId }) => {
  const members = group.members || [];
  const avatarImages = members.slice(0, 4).map((member, index) => (
    <img
      key={index}
      src={member.avatar || '/uploads/default.jpg'}
      alt={`Avatar ${index + 1}`}
      className="group-in-class-member-avatar"
    />
  ));
  const fileCount = group.project?.file_count || 1; // Số file từ project, giả định từ API
  const lastUpdated = group.updated_at ? new Date(group.updated_at).toLocaleDateString() : 'Unknown'; // Thời gian cập nhật
  const isCompleted = group.status?.toLowerCase() === 'completed';

  console.log('Group data in GroupInClass:', group);

  return (
    <div className="group-in-class-card">
      <div className="group-in-class-info">
        <div className="group-in-class-details">
          <h3 className="group-in-class-name">{group.group_name || 'Group Name'}</h3>
          <p className="group-in-class-description">{group.description || 'Description here.'}</p>
        </div>
        <div className="group-in-class-status">
          {isCompleted ? (
            <span className="group-in-class-completed">✔ Completed</span>
          ) : (
            <span className="group-in-class-ongoing">⏳ Ongoing</span>
          )}
        </div>
      </div>
      <div className="group-in-class-meta">
        <div className="group-in-class-files">
          <span className="group-in-class-file-icon">★</span>
          <span>{`${fileCount} file(s), updated ${lastUpdated}`}</span>
        </div>
        <div className="group-in-class-members">
          {avatarImages}
          <span className="group-in-class-member-count">{members.length || 0} members</span>
        </div>
      </div>
    </div>
  );
};

export default GroupInClass;
