import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import {
  AppBar,
  Toolbar,
  Typography,
  Box,
  Button,
  IconButton,
  Avatar,
  AvatarGroup,
  Select,
  MenuItem,
  InputBase,
} from '@mui/material';
import {
  ExpandMore,
  Dashboard,
  Assignment,
  List,
  CalendarToday,
  Star,
  FilterList,
  Search as SearchIcon,
  Group as GroupIcon,
} from '@mui/icons-material';
import './TeamHeader.css';

// Component hiển thị header và team header của nhóm
const TeamHeader = () => {
  // Lấy classId (thực chất là groupId) từ URL params
  const { classId } = useParams();

  // State để lưu thông tin nhóm và sprint
  const [group, setGroup] = useState(null);
  const [sprints, setSprints] = useState([]);
  const [selectedSprint, setSelectedSprint] = useState('');

  // Lấy dữ liệu nhóm và sprint từ API
  useEffect(() => {
    const fetchGroupData = async () => {
      try {
        // Lấy thông tin nhóm
        const groupResponse = await fetch(`/api/groups/${classId}`);
        const groupData = await groupResponse.json();
        setGroup(groupData);

        // Lấy danh sách sprint của nhóm
        const sprintsResponse = await fetch(`/api/sprints?groupId=${classId}`);
        const sprintsData = await sprintsResponse.json();
        setSprints(sprintsData);

        // Chọn sprint mặc định (sprint gần nhất)
        if (sprintsData.length > 0) {
          setSelectedSprint(sprintsData[0].sprint_id);
        }
      } catch (error) {
        console.error('Error fetching group data:', error);
      }
    };

    fetchGroupData();
  }, [classId]);

  // Xử lý thay đổi sprint
  const handleSprintChange = (event) => {
    setSelectedSprint(event.target.value);
  };

  // Nếu chưa có dữ liệu, hiển thị loading
  if (!group) {
    return <Typography>Loading...</Typography>;
  }

  return (
    <Box className="team-header-container">
      {/* Header chứa tên nhóm và danh sách avatar */}
      <Box className="header">
        <Box className="right-header">
          <Box className="frame-49">
            <Box className="frame-45">
              <Typography className="name-team">{group.group_name}</Typography>
              <Box className="frame-44">
                <Typography className="change-project">Change project</Typography>
                <ExpandMore className="ic-outline-expand-more" />
              </Box>
            </Box>
            <Box className="line-1" />
            <AvatarGroup max={3} className="frame-43">
              {group.members.map((member, index) => (
                <Avatar
                  key={index}
                  alt={member.username}
                  src={member.avatar}
                  className={`ellipse-${index + 4}`}
                />
              ))}
            </AvatarGroup>
          </Box>
        </Box>
      </Box>

      {/* Thanh team-header cố định khi cuộn */}
      <AppBar position="sticky" className="team-header">
        <Toolbar>
          <Box className="frame-63">
            {/* Dropdown Sprint */}
            <Box className="frame-56">
              <Typography className="sprint">Sprint</Typography>
              <Select
                value={selectedSprint}
                onChange={handleSprintChange}
                className="sprint-select"
                IconComponent={ExpandMore}
                renderValue={(selected) => {
                  const sprint = sprints.find((s) => s.sprint_id === selected);
                  return sprint ? sprint.sprint_number : '';
                }}
              >
                {sprints.map((sprint) => (
                  <MenuItem key={sprint.sprint_id} value={sprint.sprint_id}>
                    {sprint.sprint_number}
                  </MenuItem>
                ))}
              </Select>
            </Box>
            <Box className="line-8" />
            {/* Các tab điều hướng */}
            <Button className="frame-78">
              <GroupIcon className="group" />
              <Typography className="introduce">Introduce</Typography>
            </Button>
            <Button className="frame-58">
              <Dashboard className="material-symbols-dashboard-outline-rounded" />
              <Typography className="dashboard">Dashboard</Typography>
            </Button>
            <Button className="frame-59">
              <Assignment className="fluent-tasks-app-24-regular" />
              <Typography className="team-task">Team task</Typography>
            </Button>
            <Button className="frame-53">
              <List className="material-symbols-list-rounded" />
              <Typography className="my-task">My task</Typography>
            </Button>
            <Button className="frame-54">
              <CalendarToday className="mdi-calendar" />
              <Typography className="roadmap">Roadmap</Typography>
            </Button>
            <Button className="frame-55">
              <Star className="star-2" />
              <Typography className="rate">Rate</Typography>
            </Button>
          </Box>
          <Box className="frame-77">
            {/* Nút Filter */}
            <Button className="frame-76">
              <FilterList className="vector" />
              <Typography className="filter">Filter</Typography>
              <ExpandMore className="ic-outline-expand-more2" />
            </Button>
            {/* Nút Search */}
            <Box className="frame-46">
              <IconButton>
                <SearchIcon className="mynaui-search" />
              </IconButton>
              <InputBase className="search" placeholder="Search" />
            </Box>
          </Box>
        </Toolbar>
      </AppBar>
    </Box>
  );
};

export default TeamHeader;
