import React from 'react';
import PropTypes from 'prop-types';
import '../css/TeamHeader.css';

const TeamHeader = ({ teamName, members, activeTab, sprints, onSprintChange, onTabChange }) => {
  const tabs = ['Introduce', 'Team task', 'Dashboard', 'My task', 'Roadmap', 'Rate'];

  return (
    <div className="team-header">
      <div className="team-info">
        <h2>{teamName}</h2>
        <div className="avatars">
          {members.slice(0, 3).map((member, index) => (
            <img
              key={index}
              src={`/avatars/${member.avatar}`}
              alt={`Member ${member.user_id}`}
              className="avatar"
            />
          ))}
          {members.length > 3 && (
            <span className="more-members">+{members.length - 3}</span>
          )}
        </div>
      </div>
      <div className="tabs">
        {tabs.map((tab) => (
          <button
            key={tab}
            className={`tab ${activeTab === tab ? 'active' : ''}`}
            onClick={() => onTabChange(tab)}
          >
            {tab}
          </button>
        ))}
      </div>
      {activeTab === 'Team task' && (
        <div className="sprint-selector">
          <select
            onChange={(e) => {
              const selectedSprint = sprints.find(
                (sprint) => sprint.sprint_id === parseInt(e.target.value)
              );
              onSprintChange(selectedSprint);
            }}
            value={sprints.length > 0 ? sprints[sprints.length - 1].sprint_id : ''}
          >
            {sprints.map((sprint) => (
              <option key={sprint.sprint_id} value={sprint.sprint_id}>
                {sprint.sprint_name}
              </option>
            ))}
          </select>
        </div>
      )}
    </div>
  );
};

TeamHeader.propTypes = {
  teamName: PropTypes.string.isRequired,
  members: PropTypes.arrayOf(
    PropTypes.shape({
      user_id: PropTypes.number,
      avatar: PropTypes.string,
    })
  ).isRequired,
  activeTab: PropTypes.string.isRequired,
  sprints: PropTypes.arrayOf(
    PropTypes.shape({
      sprint_id: PropTypes.number,
      sprint_name: PropTypes.string,
      sprint_number: PropTypes.number,
    })
  ).isRequired,
  onSprintChange: PropTypes.func.isRequired,
  onTabChange: PropTypes.func.isRequired,
};

export default TeamHeader;
