/* frontend/src/pages/Project.js */
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import TeamHeader from '../components/shared/TeamHeader/TeamHeader';
import { fetchProjectById } from '../services/groupService'; // Sử dụng groupService
import './css/Project.css'; // Cập nhật đường dẫn mới

const Project = () => {
  const { projectId } = useParams();
  const [projectData, setProjectData] = useState(null);
  const [error, setError] = useState(null);
  const [selectedSprint, setSelectedSprint] = useState(null); // State để lưu sprint được chọn
  const [tasks, setTasks] = useState([]); // State để lưu danh sách task

  useEffect(() => {
    const loadProject = async () => {
      try {
        const data = await fetchProjectById(projectId);
        setProjectData(data);
        // Mặc định chọn sprint cuối cùng
        if (data.sprints && data.sprints.length > 0) {
          setSelectedSprint(data.sprints[data.sprints.length - 1]);
        }
      } catch (err) {
        setError('Failed to load project');
      }
    };
    loadProject();
  }, [projectId]);

  useEffect(() => {
    const loadTasks = async () => {
      if (selectedSprint) {
        try {
          const response = await fetch(`http://localhost:5000/api/tasks?sprint_id=${selectedSprint.sprint_id}`);
          if (!response.ok) {
            throw new Error('Failed to fetch tasks');
          }
          const taskData = await response.json();
          setTasks(taskData);
        } catch (err) {
          console.error('Failed to load tasks:', err);
          setTasks([]);
        }
      }
    };
    loadTasks();
  }, [selectedSprint]);

  const handleSprintChange = (sprint) => {
    setSelectedSprint(sprint);
  };

  if (error) {
    return <div>{error}</div>;
  }

  if (!projectData) {
    return <div>Loading...</div>;
  }

  return (
    <div className="project-page">
      <TeamHeader
        teamName={projectData.group_name || 'Name team'}
        members={projectData.members || []}
        activeTab="Team task" // Hiển thị tab "Team task" vì đang xem danh sách task
        sprints={projectData.sprints || []}
        onSprintChange={handleSprintChange} // Truyền hàm để xử lý thay đổi sprint
      />
      <div className="project-content">
        <h2>{projectData.project_name}</h2>
        <p>Group: {projectData.group_name}</p>
        <p>Members: {projectData.members?.length || 0}</p>
        {selectedSprint && (
          <>
            <h3>Tasks in {selectedSprint.sprint_name}</h3>
            {tasks.length > 0 ? (
              <ul>
                {tasks.map((task) => (
                  <li key={task.task_id}>
                    <strong>{task.title}</strong> - {task.description} (Status: {task.status}, Due: {new Date(task.due_date).toLocaleDateString()})
                  </li>
                ))}
              </ul>
            ) : (
              <p>No tasks found for this sprint.</p>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default Project;
