import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Sidebar from '../components/shared/SideBar/Sidebar';
import TeamHeader from '../components/shared/TeamHeader/TeamHeader';
import { fetchProjectById } from '../services/groupService';
import './css/Project.css';

const Project = () => {
  const { userId, classId, projectId, sprintId, tab } = useParams();
  const navigate = useNavigate();
  const [projectData, setProjectData] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const loadData = async () => {
      setLoading(true);
      try {
        if (!projectId || isNaN(projectId)) {
          throw new Error('projectId không hợp lệ');
        }
        const project = await fetchProjectById(projectId);
        if (!project || !project.project_id) {
          throw new Error('Không tìm thấy dự án');
        }
        setProjectData(project);

        // Nếu không có sprintId và tab trong URL, chọn sprint cuối cùng và tab "Team task"
        if (!sprintId && !tab && project.sprints && project.sprints.length > 0) {
          const lastSprint = project.sprints[project.sprints.length - 1];
          navigate(`/userID/${userId}/classes/${classId}/projects/${projectId}/sprints/${lastSprint.sprint_id}/team-task`, { replace: true });
        }
      } catch (err) {
        setError('Không thể tải dữ liệu. Vui lòng thử lại sau.');
        console.error('Error in loadData:', err.message);
      } finally {
        setLoading(false);
      }
    };
    loadData();
  }, [projectId, userId, classId, navigate, sprintId, tab]);

  const handleSprintChange = (sprint) => {
    // Khi thay đổi sprint, giữ tab hiện tại ("Team task" hoặc "My task") và cập nhật URL
    const currentTab = activeTab.toLowerCase().replace(' ', '-');
    navigate(`/userID/${userId}/classes/${classId}/projects/${projectId}/sprints/${sprint.sprint_id}/${currentTab}`);
  };

  const handleTabChange = (tabName) => {
    // Nếu chuyển sang "Team task" hoặc "My task" mà chưa có sprintId, chọn sprint cuối cùng
    if ((tabName === 'Team task' || tabName === 'My task') && !sprintId && projectData?.sprints?.length > 0) {
      const lastSprint = projectData.sprints[projectData.sprints.length - 1];
      navigate(`/userID/${userId}/classes/${classId}/projects/${projectId}/sprints/${lastSprint.sprint_id}/${tabName.toLowerCase().replace(' ', '-')}`);
    } else {
      // Đối với các tab khác (Introduce, Dashboard, Roadmap, Rate), không cần sprintId
      const tabPath = tabName.toLowerCase().replace(' ', '-');
      if (tabName === 'Team task' || tabName === 'My task') {
        // Giữ sprintId hiện tại nếu có
        navigate(`/userID/${userId}/classes/${classId}/projects/${projectId}/sprints/${sprintId || projectData?.sprints[projectData.sprints.length - 1]?.sprint_id}/${tabPath}`);
      } else {
        navigate(`/userID/${userId}/classes/${classId}/projects/${projectId}/${tabPath}`);
      }
    }
  };

  const activeTab = tab?.replace('-', ' ') || (sprintId ? 'Team task' : 'Introduce');

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  if (loading || !projectData) {
    return <div className="loading">Đang tải dữ liệu...</div>;
  }

  return (
    <div className="project-page">
      <Sidebar />
      <div className="content-container">
        <TeamHeader
          className={projectData.class_name || 'Tên lớp'}
          classCode={projectData.class_code || '100001'}
          teamName={projectData.group_name || 'Tên nhóm'}
          projectName={projectData.project_name || 'Tên dự án'}
          members={projectData.members || []}
          activeTab={activeTab}
          sprints={projectData.sprints || []}
          selectedSprintId={sprintId}
          onSprintChange={handleSprintChange}
          onTabChange={handleTabChange}
        />
        <div className="project-content">
          {/* Nội dung các tab sẽ do người khác làm, không cần hiển thị ở đây */}
          <p>Tab hiện tại: {activeTab}</p>
          {sprintId && <p>Sprint hiện tại: {sprintId}</p>}
        </div>
      </div>
    </div>
  );
};

export default Project;
