import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Sidebar from '../components/shared/SideBar/Sidebar';
import TeamHeader from '../components/shared/TeamHeader/TeamHeader';
import { fetchGroupMembers, fetchPeerAssessments, savePeerAssessment, fetchProjectById } from '../services/groupService';

const ProjectRate = () => {
  const { userId, classId, projectId, sprintId } = useParams();
  const [projectData, setProjectData] = useState(null);
  const [members, setMembers] = useState([]);
  const [assessments, setAssessments] = useState({});
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        // Lấy thông tin dự án
        const project = await fetchProjectById(projectId);
        setProjectData(project);

        // Lấy danh sách thành viên
        const membersData = await fetchGroupMembers(projectId);
        setMembers(membersData);

        // Lấy đánh giá hiện có
        const assessmentsData = await fetchPeerAssessments(projectId, userId);
        const existingAssessments = assessmentsData.reduce((acc, item) => {
          acc[item.assessee_id] = {
            deadlineScore: item.deadline_score || 0,
            friendlyScore: item.friendly_score || 0,
            qualityScore: item.quality_score || 0,
            teamSupportScore: item.team_support_score || 0,
            responsibilityScore: item.responsibility_score || 0,
            note: item.note || '',
          };
          return acc;
        }, {});
        setAssessments(existingAssessments);
      } catch (err) {
        setError('Không thể tải dữ liệu. Vui lòng thử lại sau.');
        console.error('Error fetching data:', err);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [projectId, userId]);

  const handleRatingChange = (assesseeId, category, value) => {
    setAssessments((prev) => ({
      ...prev,
      [assesseeId]: {
        ...prev[assesseeId],
        [category]: value,
      },
    }));
  };

  const handleNoteChange = (assesseeId, value) => {
    setAssessments((prev) => ({
      ...prev,
      [assesseeId]: {
        ...prev[assesseeId],
        note: value,
      },
    }));
  };

  const handleSubmit = async () => {
    setLoading(true);
    try {
      for (const assesseeId in assessments) {
        await savePeerAssessment(projectId, {
          assessorId: parseInt(userId, 10),
          assesseeId: parseInt(assesseeId, 10),
          ...assessments[assesseeId],
          projectId: parseInt(projectId, 10),
        });
      }
      alert('Đánh giá đã được lưu thành công!');
    } catch (err) {
      setError('Lỗi khi lưu đánh giá. Vui lòng thử lại.');
      console.error('Error saving assessments:', err);
    } finally {
      setLoading(false);
    }
  };

  if (error) return <div className="error-message">{error}</div>;
  if (loading || !projectData) return <div className="loading">Đang tải...</div>;

  return (
    <div className="project-page">
      <Sidebar />
      <div className="content-container">
        <TeamHeader
          className={projectData.class_name || 'Tên lớp'}
          classCode={projectData.class_id || '100001'}
          teamName={projectData.group_name || 'Name team'}
          projectName={projectData.project_name || 'Team project'}
          members={projectData.members || []}
          activeTab="Rate"
          sprints={projectData.sprints || []}
          selectedSprintId={sprintId}
          onSprintChange={() => {}}
          onTabChange={() => {}}
        />
        <div className="project-content">
          <h2>Đánh giá thành viên</h2>
          {members.map((member) => (
            <div key={member.user_id} className="task-item">
              <div className="member-info">
                <img src={member.avatar} alt={member.username} className="member-avatar" />
                <h4>{member.username}</h4>
              </div>
              <div className="rating-section">
                <div>
                  <label>Deadline:</label>
                  <select
                    value={assessments[member.user_id]?.deadlineScore || 0}
                    onChange={(e) => handleRatingChange(member.user_id, 'deadlineScore', parseInt(e.target.value))}
                  >
                    {[0, 1, 2, 3, 4, 5].map((value) => (
                      <option key={value} value={value}>{value}</option>
                    ))}
                  </select>
                </div>
                <div>
                  <label>Friendly:</label>
                  <select
                    value={assessments[member.user_id]?.friendlyScore || 0}
                    onChange={(e) => handleRatingChange(member.user_id, 'friendlyScore', parseInt(e.target.value))}
                  >
                    {[0, 1, 2, 3, 4, 5].map((value) => (
                      <option key={value} value={value}>{value}</option>
                    ))}
                  </select>
                </div>
                <div>
                  <label>Quality of work:</label>
                  <select
                    value={assessments[member.user_id]?.qualityScore || 0}
                    onChange={(e) => handleRatingChange(member.user_id, 'qualityScore', parseInt(e.target.value))}
                  >
                    {[0, 1, 2, 3, 4, 5].map((value) => (
                      <option key={value} value={value}>{value}</option>
                    ))}
                  </select>
                </div>
                <div>
                  <label>Team Support:</label>
                  <select
                    value={assessments[member.user_id]?.teamSupportScore || 0}
                    onChange={(e) => handleRatingChange(member.user_id, 'teamSupportScore', parseInt(e.target.value))}
                  >
                    {[0, 1, 2, 3, 4, 5].map((value) => (
                      <option key={value} value={value}>{value}</option>
                    ))}
                  </select>
                </div>
                <div>
                  <label>Responsibility:</label>
                  <select
                    value={assessments[member.user_id]?.responsibilityScore || 0}
                    onChange={(e) => handleRatingChange(member.user_id, 'responsibilityScore', parseInt(e.target.value))}
                  >
                    {[0, 1, 2, 3, 4, 5].map((value) => (
                      <option key={value} value={value}>{value}</option>
                    ))}
                  </select>
                </div>
                <div>
                  <label>Ghi chú:</label>
                  <textarea
                    value={assessments[member.user_id]?.note || ''}
                    onChange={(e) => handleNoteChange(member.user_id, e.target.value)}
                  />
                </div>
              </div>
              <div className="action-buttons">
                <button>Cancel</button>
                <button onClick={handleSubmit}>Confirm</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProjectRate;
