import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Sidebar from '../components/shared/SideBar/Sidebar';
import TeamHeader from '../components/shared/TeamHeader/TeamHeader';
import { fetchGroupMembers, fetchPeerAssessments, savePeerAssessment, fetchProjectById, fetchTaskStats, fetchMemberTaskStats } from '../services/groupService';
import avatarMap from '../utils/avatarMap';

const ProjectRate = () => {
  const { userId, classId, projectId, sprintId } = useParams();
  const [projectData, setProjectData] = useState(null);
  const [members, setMembers] = useState([]);
  const [assessments, setAssessments] = useState({});
  const [taskStats, setTaskStats] = useState({ totalTasks: 0, tasksDone: 0, delayedTasks: 0 });
  const [memberTaskStats, setMemberTaskStats] = useState({});
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        console.log(`Gọi API với projectId: ${projectId}, userId: ${userId}`);
        const project = await fetchProjectById(projectId);
        setProjectData(project);

        const membersData = await fetchGroupMembers(projectId);
        setMembers(membersData);

        const assessmentsData = await fetchPeerAssessments(projectId, userId);
        console.log('Dữ liệu đánh giá:', assessmentsData);
        const existingAssessments = assessmentsData.reduce((acc, item) => {
          acc[item.assessee_id] = {
            deadlineScore: item.deadline_score || 0,
            friendlyScore: item.friendly_score || 0,
            qualityScore: item.quality_score || 0,
            teamSupportScore: item.team_support_score || 0,
            responsibilityScore: item.responsibility_score || 0,
            note: item.note || '',
          };
          return acc;
        }, {});
        setAssessments(existingAssessments);

        const taskStatsData = await fetchTaskStats(projectId);
        setTaskStats(taskStatsData);

        const memberTaskStatsData = await fetchMemberTaskStats(projectId);
        setMemberTaskStats(memberTaskStatsData);
      } catch (err) {
        setError('Không thể tải dữ liệu. Vui lòng thử lại sau.');
        console.error('Error fetching data:', err.message, err.stack);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [projectId, userId]);

  const handleRatingChange = (assesseeId, category, value) => {
    setAssessments((prev) => ({
      ...prev,
      [assesseeId]: {
        ...prev[assesseeId],
        [category]: value,
      },
    }));
  };

  const handleNoteChange = (assesseeId, value) => {
    setAssessments((prev) => ({
      ...prev,
      [assesseeId]: {
        ...prev[assesseeId],
        note: value,
      },
    }));
  };

  const handleSubmit = async () => {
    setLoading(true);
    try {
      for (const assesseeId in assessments) {
        await savePeerAssessment(projectId, {
          assessorId: parseInt(userId, 10),
          assesseeId: parseInt(assesseeId, 10),
          ...assessments[assesseeId],
          projectId: parseInt(projectId, 10),
        });
      }
      alert('Đánh giá đã được lưu thành công!');
    } catch (err) {
      setError('Lỗi khi lưu đánh giá. Vui lòng thử lại.');
      console.error('Error saving assessments:', err.message, err.stack);
    } finally {
      setLoading(false);
    }
  };

  const renderStars = (assesseeId, category, value) => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(
        <span
          key={i}
          className={`star ${i <= value ? 'filled' : ''}`}
          onClick={() => handleRatingChange(assesseeId, category, i)}
        >
          ★
        </span>
      );
    }
    return stars;
  };

  const calculateTotalRate = (assesseeId) => {
    const scores = assessments[assesseeId] || {};
    const { deadlineScore = 0, friendlyScore = 0, qualityScore = 0, teamSupportScore = 0, responsibilityScore = 0 } = scores;
    const total = (deadlineScore + friendlyScore + qualityScore + teamSupportScore + responsibilityScore) / 5;
    return Math.round(total);
  };

  const renderTotalRateStars = (assesseeId) => {
    const totalRate = calculateTotalRate(assesseeId);
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(
        <span key={i} className={`star ${i <= totalRate ? 'filled' : ''}`}>
          ★
        </span>
      );
    }
    return stars;
  };

  if (error) return <div className="error-message">{error}</div>;
  if (loading || !projectData) return <div className="loading">Đang tải...</div>;

  return (
    <div className="project-page">
      <Sidebar />
      <div className="content-container">
        <TeamHeader
          className={projectData.class_name || 'Tên lớp'}
          classCode={projectData.class_id || '100001'}
          teamName={projectData.group_name || 'Name team'}
          projectName={projectData.project_name || 'Team project'}
          members={projectData.members || []}
          activeTab="Rate"
          sprints={projectData.sprints || []}
          selectedSprintId={sprintId}
          onSprintChange={() => {}}
          onTabChange={() => {}}
        />
        <div className="project-content">
          <h2>Đánh giá thành viên</h2>
          <div className="task-stats">
            <p><strong>Total Task Done:</strong> {taskStats.tasksDone}/{taskStats.totalTasks}</p>
            <p><strong>Delay Deadline:</strong> {taskStats.delayedTasks}</p>
          </div>
          {members.map((member, index) => (
            <div key={member.user_id} className="task-item">
              <div className="member-header">
                <div className="member-info">
                  <img 
                    src={avatarMap[member.avatar] || '/assets/images/default-avatar.jpg'}
                    alt={member.username} 
                    className="member-avatar" 
                  />
                  <div>
                    <h4>{`${index + 1}. ${member.username}`}</h4>
                  </div>
                </div>
                <div className="member-stats">
                  TOTAL TASK DONE: {memberTaskStats[member.user_id]?.tasksCompleted || 0}/10 DELAY DEADLINE: {memberTaskStats[member.user_id]?.delayedTasks || 0}
                </div>
              </div>
              <div className="rating-section">
                <div className="rating-column">
                  <div className="rating-row">
                    <label>Deadline</label>
                    <div className="stars">
                      {renderStars(member.user_id, 'deadlineScore', assessments[member.user_id]?.deadlineScore || 0)}
                    </div>
                  </div>
                  <div className="rating-row">
                    <label>Friendly</label>
                    <div className="stars">
                      {renderStars(member.user_id, 'friendlyScore', assessments[member.user_id]?.friendlyScore || 0)}
                    </div>
                  </div>
                  <div className="rating-row">
                    <label>Responsibility</label>
                    <div className="stars">
                      {renderStars(member.user_id, 'responsibilityScore', assessments[member.user_id]?.responsibilityScore || 0)}
                    </div>
                  </div>
                </div>
                <div className="rating-column">
                  <div className="rating-row">
                    <label>Quality of work</label>
                    <div className="stars">
                      {renderStars(member.user_id, 'qualityScore', assessments[member.user_id]?.qualityScore || 0)}
                    </div>
                  </div>
                  <div className="rating-row">
                    <label>Team Support</label>
                    <div className="stars">
                      {renderStars(member.user_id, 'teamSupportScore', assessments[member.user_id]?.teamSupportScore || 0)}
                    </div>
                  </div>
                  <div className="rating-row">
                    <label>TOTAL RATE</label>
                    <div className="stars">
                      {renderTotalRateStars(member.user_id)}
                    </div>
                  </div>
                </div>
              </div>
              <div className="note-section">
                <label>Note</label>
                <textarea
                  value={assessments[member.user_id]?.note || ''}
                  onChange={(e) => handleNoteChange(member.user_id, e.target.value)}
                  placeholder="Enter your note here..."
                />
              </div>
              <div className="action-buttons">
                <button className="cancel-button">CANCEL</button>
                <button className="confirm-button" onClick={handleSubmit}>CONFIRM</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProjectRate;
