import React from 'react';
import avatarMap from '../../../utils/avatarMap';
import placeholderMember from '../../../assets/images/placeholders/placeholder-member.jpg';
import './TeamHeader.css';

const TeamHeader = ({ className, classCode, teamName, projectName, members, activeTab, sprints, onSprintChange, onTabChange }) => {
  const tabs = ['Introduce', 'Dashboard', 'Team task', 'My task', 'Roadmap', 'Rate'];

  return (
    <div className="team-header">
      {/* Top bar: Class, Team, and Project Info */}
      <div className="team-header-top">
        <div className="team-info">
          <p><strong>{className}</strong></p>
          <p><strong>{classCode}</strong></p>
          <p><strong>{teamName}</strong> </p>
          <p><strong>{projectName}</strong> </p>
        </div>
        <div className="team-members">
          {members && members.length > 0 ? (
            members.slice(0, 3).map((member, index) => (
              <div key={index} className="team-member">
                <img
                  src={avatarMap[member.avatar] || placeholderMember}
                  alt={`Member ${index + 1}`}
                  className="member-avatar"
                  onError={(e) => {
                    e.target.src = placeholderMember;
                  }}
                />
              </div>
            ))
          ) : (
            <p>No members</p>
          )}
          {members.length > 3 && (
            <div className="more-members">+{members.length - 3}</div>
          )}
        </div>
      </div>

      {/* Bottom bar: Tabs and Filter/Search */}
      <div className="team-header-tabs">
        <div className="tabs">
          {tabs.map((tab, index) => (
            <button
              key={index}
              className={`tab ${activeTab === tab ? 'active' : ''}`}
              onClick={() => onTabChange(tab)}
            >
              {tab}
            </button>
          ))}
        </div>
        <div className="actions">
          <button className="filter-btn">Filter</button>
          <div className="search-bar">
            <input type="text" placeholder="Search" />
            <span className="search-icon"></span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TeamHeader;
