import React, { useState } from 'react';
import LinkIcon from '@mui/icons-material/Link';
import DeleteIcon from '@mui/icons-material/Delete';

const ClassCardMenu = ({ classId, onGetLink, onDelete }) => {
  const [isOpen, setIsOpen] = useState(false);

  const handleToggle = () => {
    setIsOpen(!isOpen);
  };

  const handleGetLink = () => {
    if (onGetLink) onGetLink(classId);
    setIsOpen(false);
  };

  const handleDelete = () => {
    if (onDelete) onDelete(classId);
    setIsOpen(false);
  };

  return (
    <div className="classcard-menu" style={{ position: 'relative' }}>
      <button onClick={handleToggle} className="classcard-menu-button" style={{ background: 'none', border: 'none', cursor: 'pointer', fontSize: '20px' }}>
        …
      </button>
      {isOpen && (
        <div className="classcard-menu-dropdown" style={{ position: 'absolute', top: '100%', right: '0', background: '#fff', boxShadow: '0 2px 5px rgba(0,0,0,0.2)', borderRadius: '4px', padding: '5px 0', zIndex: 10 }}>
          <button onClick={handleGetLink} className="classcard-menu-item" style={{ display: 'flex', alignItems: 'center', width: '100%', padding: '5px 10px', textAlign: 'left', background: 'none', border: 'none', cursor: 'pointer' }}>
            <LinkIcon style={{ marginRight: '8px', fontSize: '18px', color: '#4a90e2' }} />
            Lấy link
          </button>
          <button onClick={handleDelete} className="classcard-menu-item" style={{ display: 'flex', alignItems: 'center', width: '100%', padding: '5px 10px', textAlign: 'left', background: 'none', border: 'none', cursor: 'pointer', color: '#ff4444' }}>
            <DeleteIcon style={{ marginRight: '8px', fontSize: '18px', color: '#ff4444' }} />
            Xóa lớp
          </button>
        </div>
      )}
    </div>
  );
};

export default ClassCardMenu;
