// frontend/src/services/api-client.js
export const fetchTasks = async () => {
  const response = await fetch("/api/tasks", {
    method: "GET",
    headers: {
      "Content-Type": "application/json",
    },
  });
  if (!response.ok) {
    throw new Error("Failed to fetch tasks");
  }
  return response.json();
};

export const updateChecklistItem = async (checklistId, isCompleted) => {
  const response = await fetch(`/api/task-checklists/${checklistId}`, {
    method: "PATCH",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({ is_completed: isCompleted ? 1 : 0 }), // Convert boolean to 0/1 for MySQL
  });
  if (!response.ok) {
    throw new Error("Failed to update checklist item");
  }
  return response.json();
};
