import React from "react";
import "..//assets/styles/global.css";
import "./ReportPage.css";

const ReportPage = () => {
  return (
    <div className="report-container">
      {/* Tiêu đề */}
      <div className="report-header">
        <h1 className="h1">Báo cáo</h1>
        <select className="body_2 report-select">
          <option value="week">Tuần này</option>
          <option value="month">Tháng này</option>
          <option value="quarter">Quý này</option>
        </select>
      </div>

      <h4 className="h4 report-subtitle">Tổng quan báo cáo hệ thống</h4>

      {/* Bảng dữ liệu */}
      <table className="report-table">
        <thead>
          <tr className="body_2">
            <th>STT</th>
            <th>Tên nhóm</th>
            <th>Tổng số nhiệm vụ</th>
            <th>Đã hoàn thành</th>
            <th>Tiến độ</th>
          </tr>
        </thead>
        <tbody>
          {[1, 2, 3].map((i) => (
            <tr key={i} className="body_1">
              <td>{i}</td>
              <td>Nhóm {i}</td>
              <td>{5 * i}</td>
              <td>{3 * i}</td>
              <td>{Math.floor((3 * i * 100) / (5 * i))}%</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Tổng kết */}
      <div className="report-summary">
        <h2 className="h2">Tổng kết</h2>
        <p className="body_1">Số nhóm hoạt động tốt: 2</p>
        <p className="body_1">Số nhóm cần hỗ trợ thêm: 1</p>
      </div>
    </div>
  );
};

export default ReportPage;
