// frontend/src/services/api-client.js
const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || "http://localhost:5000";

export const fetchTasks = async () => {
  const response = await fetch(`${API_BASE_URL}/api/tasks`, {
    method: "GET",
    headers: {
      "Content-Type": "application/json",
    },
  });
  if (!response.ok) {
    throw new Error("Failed to fetch tasks");
  }
  const data = await response.json();
  return data.map((task) => ({
    task_id: task.task_id,
    title: task.title,
    due_date: task.due_date,
    status: task.status,
    checklists: task.checklists && task.checklists.length > 0 ? task.checklists : [], // Ensure empty array for no subtasks
    comment_count: task.comment_count || 0,
    progress_percentage: parseInt(task.progress_percentage) || 0,
    assigned_to: task.assigned_to || task.task_id,
  }));
};

export const updateChecklistItem = async (checklistId, isCompleted) => {
  const response = await fetch(`${API_BASE_URL}/api/task-checklists/${checklistId}`, {
    method: "PATCH",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({ is_completed: isCompleted }),
  });
  if (!response.ok) {
    throw new Error("Failed to update checklist item");
  }
  return response.json();
};

export const updateTaskStatus = async (taskId, status) => {
  const response = await fetch(`${API_BASE_URL}/api/tasks/${taskId}/status`, {
    method: "PATCH",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      status: status === "to-do" ? "To-Do" : status === "in-progress" ? "In-Progress" : "Completed",
    }),
  });
  if (!response.ok) {
    throw new Error("Failed to update task status");
  }
  return response.json();
};

export const fetchProjects = async () => {
  const response = await fetch(`${API_BASE_URL}/api/projects`, {
    method: "GET",
    headers: {
      "Content-Type": "application/json",
    },
  });
  if (!response.ok) {
    throw new Error("Failed to fetch projects");
  }
  return response.json();
};

export const fetchSprints = async () => {
  const response = await fetch(`${API_BASE_URL}/api/sprints`, {
    method: "GET",
    headers: {
      "Content-Type": "application/json",
    },
  });
  if (!response.ok) {
    throw new Error("Failed to fetch sprints");
  }
  return response.json();
};

export const createTask = async (taskData) => {
  const response = await fetch(`${API_BASE_URL}/api/tasks`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      ...taskData,
      status: taskData.status === "to-do" ? "To-Do" : taskData.status === "in-progress" ? "In-Progress" : "To-Do",
    }),
  });
  if (!response.ok) {
    throw new Error("Failed to create task");
  }
  return response.json();
};
