import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Sidebar from '../components/shared/SideBar/Sidebar';
import ClassCard from '../components/shared/ClassCard/ClassCard';
import { fetchGroupsByUserId, fetchProjectByClassId } from '../services/groupService';
import './css/GroupPage.css';

const GroupPage = () => {
  const { userId, classId } = useParams();
  const navigate = useNavigate();
  const [groups, setGroups] = useState([]);
  const [projectData, setProjectData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadData = async () => {
      try {
        if (classId) {
          // Nếu có classId, lấy thông tin dự án
          const project = await fetchProjectByClassId(userId, classId);
          if (project && project.project_id) {
            setProjectData(project);
          } else {
            setError('Không tìm thấy dự án cho lớp học này');
          }
        } else {
          // Nếu không có classId, lấy danh sách nhóm
          const data = await fetchGroupsByUserId(userId);
          setGroups(data);
        }
      } catch (err) {
        setError('Không thể tải dữ liệu. Vui lòng thử lại sau.');
      }
    };
    loadData();
  }, [userId, classId]);

  const handleClassCardClick = async (classId) => {
    navigate(`/userID/${userId}/classes/${classId}/projects`);
  };

  const handleSprintChange = (sprint) => {
    navigate(`/userID/${userId}/classes/${classId}/projects/${projectData.project_id}/sprints/${sprint.sprint_id}`);
  };

  const handleTabChange = (tabName) => {
    const tabPath = tabName === 'Team task' ? `sprints/${projectData.sprints[projectData.sprints.length - 1]?.sprint_id || ''}` : tabName.toLowerCase().replace(' ', '-');
    navigate(`/userID/${userId}/classes/${classId}/projects/${projectData.project_id}/${tabPath}`);
  };

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="group-page">
      <Sidebar />
      <div className="content-container">
        {classId && projectData ? (
          <>
            <TeamHeader
              teamName={projectData.group_name || 'Name team'}
              members={projectData.members || []}
              activeTab="Introduce"
              sprints={projectData.sprints || []}
              onSprintChange={handleSprintChange}
              onTabChange={handleTabChange}
            />
            <div className="project-content">
              <h3>Introduce</h3>
              <p>Project: {projectData.project_name}</p>
              <p>Group: {projectData.group_name}</p>
              <p>Members: {projectData.members?.length || 0}</p>
            </div>
          </>
        ) : (
          <>
            <h1>Danh sách nhóm</h1>
            <div className="class-card-container">
              {groups.length > 0 ? (
                groups.map((group, index) => (
                  <ClassCard
                    key={index}
                    classId={group.class_id}
                    className={group.class_name}
                    groupName={group.group_name}
                    projectName={group.project_name}
                    memberCount={group.member_count}
                    avatarNumber={group.avatar_number}
                    avatarColor={group.avatar_color}
                    members={group.members}
                    onClick={() => handleClassCardClick(group.class_id)}
                  />
                ))
              ) : (
                <p>Không có nhóm nào để hiển thị.</p>
              )}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default GroupPage;
