import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { fetchGroupsByUserId } from '../../../services/groupService';
import avatarMap from '../../../utils/avatarMap';
import placeholderMember from '../../../assets/images/placeholders/placeholder-member.jpg';
import './TeamHeader.css';

const TeamHeader = ({ teamName = 'Name team', members = [], activeTab = 'Team task', sprints = [], onSprintChange, onTabChange }) => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const [groups, setGroups] = useState([]);
  const [selectedGroup, setSelectedGroup] = useState(teamName);

  const tabs = [
    { name: 'Introduce', icon: '📋' },
    { name: 'Dashboard', icon: '📊' },
    { name: 'Team task', icon: '📝' },
    { name: 'My task', icon: '📋' },
    { name: 'Roadmap', icon: '🗺️' },
    { name: 'Rate', icon: '⭐' },
  ];

  const [selectedSprint, setSelectedSprint] = useState(sprints.length > 0 ? sprints[0] : null);

  useEffect(() => {
    const loadGroups = async () => {
      try {
        const data = await fetchGroupsByUserId(userId);
        setGroups(data);
      } catch (err) {
        console.error('Không thể tải danh sách nhóm:', err);
      }
    };
    loadGroups();
  }, [userId]);

  useEffect(() => {
    if (sprints.length > 0 && !selectedSprint) {
      const sprint1 = sprints.find(sprint => sprint.sprint_number === 1) || sprints[0];
      setSelectedSprint(sprint1);
      onSprintChange?.(sprint1);
    }
  }, [sprints, selectedSprint, onSprintChange]);

  const handleSprintChange = (e) => {
    const sprintName = e.target.value;
    const sprint = sprints.find((s) => s.sprint_name === sprintName);
    setSelectedSprint(sprint);
    onSprintChange?.(sprint);
  };

  const handleGroupChange = async (e) => {
    const groupName = e.target.value;
    const selected = groups.find((group) => group.groupName === groupName);
    setSelectedGroup(groupName);
    if (selected) {
      try {
        const response = await fetch(`http://localhost:5000/api/groups/projects/by-class?class_id=${selected.classId}&user_id=${userId}`);
        const project = await response.json();
        if (project && project.project_id) {
          navigate(`/projects/${project.project_id}`);
        }
      } catch (err) {
        console.error('Không thể đổi nhóm:', err);
      }
    }
  };

  return (
    <div className="team-header">
      <div className="team-header-left">
        <select
          className="team-name"
          value={selectedGroup}
          onChange={handleGroupChange}
        >
          {groups.length > 0 ? (
            groups.map((group) => (
              <option key={group.classId} value={group.groupName}>
                {group.groupName}
              </option>
            ))
          ) : (
            <option value={teamName}>{teamName}</option>
          )}
        </select>
        <div className="team-dropdown">
          Change project
          <span className="dropdown-arrow">▼</span>
        </div>
      </div>

      <div className="team-members">
        {members && members.length > 0 ? (
          members.slice(0, 3).map((member, index) => (
            <div key={index} className="team-member">
              <img
                className="team-member-img"
                src={avatarMap[member.avatar] || placeholderMember}
                alt={`Member ${index + 1}`}
                onError={(e) => {
                  e.target.src = placeholderMember;
                }}
              />
            </div>
          ))
        ) : (
          <p>No members</p>
        )}
      </div>

      <div className="team-header-right">
        <div className="team-tabs">
          <select
            className="sprint-label"
            value={selectedSprint?.sprint_name || ''}
            onChange={handleSprintChange}
          >
            {sprints.length > 0 ? (
              sprints.map((sprint) => (
                <option key={sprint.sprint_id} value={sprint.sprint_name}>
                  {sprint.sprint_name}
                </option>
              ))
            ) : (
              <option value="Sprint 4">Sprint 4</option>
            )}
          </select>
          {tabs.map((tab) => (
            <div
              key={tab.name}
              className={`team-tab ${activeTab === tab.name ? 'active' : ''}`}
              onClick={() => onTabChange?.(tab.name)}
            >
              <span className="tab-icon">{tab.icon}</span>
              {tab.name}
            </div>
          ))}
        </div>
        <div className="team-actions">
          <button className="filter-btn">
            Filter ▼
          </button>
          <button className="search-btn">
            Search 🔍
          </button>
        </div>
      </div>
    </div>
  );
};

export default TeamHeader;
