import React from "react";
import "../../assets/styles/global.css";
import "./KanbanView.css";

const ReportPage = () => {
  const reportData = [
    { id: 1, name: "Nhóm 1", total: 10, done: 7 },
    { id: 2, name: "Nhóm 2", total: 8, done: 4 },
    { id: 3, name: "Nhóm 3", total: 5, done: 5 },
  ];

  const getProgressColor = (percent) => {
    if (percent >= 80) return "green";
    if (percent >= 50) return "yellow";
    return "red";
  };

  return (
    <div className="report-wrapper">
      <div className="report-header">
        <h1 className="h1">Team Report</h1>
        <select className="report-select body_2">
          <option value="week">Sprint 4</option>
          <option value="sprint3">Sprint 3</option>
        </select>
      </div>

      <h4 className="h4 report-subtitle">Tổng quan tiến độ của các nhóm</h4>

      <div className="report-grid">
        {reportData.map((item) => {
          const percent = Math.round((item.done / item.total) * 100);
          const progressClass = getProgressColor(percent);

          return (
            <div key={item.id} className="report-card">
              <div className="report-card-header">
                <h3 className="h3">{item.name}</h3>
                <div className={`progress-dot ${progressClass}`}></div>
              </div>
              <div className="report-card-body body_2">
                <p>Tổng nhiệm vụ: {item.total}</p>
                <p>Hoàn thành: {item.done}</p>
                <p>Tiến độ: {percent}%</p>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default KanbanView;
