import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Sidebar from '../components/shared/SideBar/Sidebar';
import GroupInClass from '../components/shared/GroupInClass/GroupInClass';
import { fetchGroupsByUserId } from '../services/groupService';
import './css/ClassGroupPage.css';
const ClassGroupsPage = () => {
  const { userId, classId } = useParams();
  const [groups, setGroups] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const loadData = async () => {
      setLoading(true);
      try {
        if (!userId || isNaN(userId) || !classId || isNaN(classId)) {
          throw new Error('userId hoặc classId không hợp lệ');
        }
        const data = await fetchGroupsByUserId(userId);
        // Lọc các nhóm thuộc classId hiện tại
        const filteredGroups = data.filter(group => group.classId === parseInt(classId));
        if (filteredGroups.length === 0) {
          throw new Error('Không tìm thấy nhóm nào trong lớp học');
        }
        setGroups(filteredGroups);
      } catch (err) {
        setError('Không thể tải dữ liệu nhóm. Vui lòng thử lại sau.');
        console.error('Error in loadData:', err.message);
      } finally {
        setLoading(false);
      }
    };
    loadData();
  }, [userId, classId]);

  if (error) {
    return (
      <div className="class-groups-page">
        <Sidebar userId={userId} />
        <div className="class-groups-content">
          <div className="error-message">{error}</div>
        </div>
      </div>
    );
  }

  return (
    <div className="class-groups-page">
      <Sidebar userId={userId} />
      <div className="class-groups-content">
        <div className="class-groups-header">
          <h1>WorkTrace</h1>
          <div className="class-info">
            <h2>Name Class 1</h2>
            <div className="class-nav">
              <a href={`/userID/${userId}/classes/${classId}/members`}>Members</a>
              <a href={`/userID/${userId}/classes/${classId}/settings`}>Settings</a>
            </div>
          </div>
        </div>
        <div className="class-groups-main">
          <div className="sidebar-placeholder">
            <h3>Student list management</h3>
            <ul>
              <li>Teams</li>
              <li>Thông báo</li>
            </ul>
          </div>
          <div className="groups-list">
            {loading ? (
              <p>Đang tải...</p>
            ) : groups.length > 0 ? (
              groups.map((group, index) => (
                <GroupInClass
                  key={index}
                  group={group}
                  userId={userId}
                  classId={classId}
                />
              ))
            ) : (
              <p>Không có nhóm nào để hiển thị.</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ClassGroupsPage;
