// frontend/src/components/task/CreateTask.js
import React, { useState, useEffect } from "react";
import { fetchUsersByProject, createTask } from "../../services/api-client";
import "./CreateTask.css";

const CreateTask = ({ onTaskCreated, onCancel, selectedSprintId }) => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [assignedTo, setAssignedTo] = useState("");
  const [dueDate, setDueDate] = useState("");
  const [subTasks, setSubTasks] = useState([""]);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const loadUsers = async () => {
      try {
        const data = await fetchUsersByProject();
        setUsers(data);
        if (data.length > 0) {
          setAssignedTo(data[0].user_id); // Mặc định chọn user đầu tiên
        }
      } catch (error) {
        console.error("Error fetching users:", error);
      }
    };
    loadUsers();
  }, []);

  const addSubTask = () => {
    setSubTasks([...subTasks, ""]);
  };

  const removeSubTask = (index) => {
    const newSubTasks = subTasks.filter((_, i) => i !== index);
    setSubTasks(newSubTasks);
  };

  const handleSubTaskChange = (index, value) => {
    const newSubTasks = [...subTasks];
    newSubTasks[index] = value;
    setSubTasks(newSubTasks);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const taskData = {
        title,
        description,
        assigned_to: parseInt(assignedTo),
        due_date: dueDate,
        status: "To-Do", // Explicitly set to To-Do
        subtasks: subTasks.filter((subtask) => subtask.trim() !== ""),
        sprint_id: selectedSprintId || 1, // Sử dụng selectedSprintId từ props
      };

      await createTask(taskData);
      alert("Task created successfully!");
      setTitle("");
      setDescription("");
      setAssignedTo(users[0]?.user_id || "");
      setDueDate("");
      setSubTasks([""]);
      if (onTaskCreated) {
        onTaskCreated();
      }
    } catch (error) {
      console.error("Error creating task:", error);
      alert("Failed to create task.");
    }
  };

  const handleCancelClick = () => {
    if (onCancel) {
      onCancel();
    }
  };

  return (
    <div className="create-task-container">
      <h2>Create New Task</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="title">Task Title:</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            rows="4"
          />
        </div>

        <div className="form-group">
          <label htmlFor="assignedTo">Assigned To:</label>
          <select
            id="assignedTo"
            value={assignedTo}
            onChange={(e) => setAssignedTo(e.target.value)}
            required
          >
            {users.map((user) => (
              <option key={user.user_id} value={user.user_id}>
                {user.username}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="dueDate">Deadline:</label>
          <input
            type="date"
            id="dueDate"
            value={dueDate}
            onChange={(e) => setDueDate(e.target.value)}
          />
        </div>

        <div className="form-group">
          <label>Subtasks:</label>
          {subTasks.map((subTask, index) => (
            <div key={index} className="subtask-input">
              <input
                type="text"
                value={subTask}
                onChange={(e) => handleSubTaskChange(index, e.target.value)}
                placeholder={`Subtask ${index + 1}`}
              />
              {subTasks.length > 1 && (
                <button
                  type="button"
                  onClick={() => removeSubTask(index)}
                  className="remove-subtask-btn"
                >
                  Remove
                </button>
              )}
            </div>
          ))}
          <button type="button" onClick={addSubTask} className="add-subtask-btn">
            Add Subtask
          </button>
        </div>

        <div className="form-buttons">
          <button type="submit" className="submit-btn">
            Create Task
          </button>
          <button type="button" onClick={handleCancelClick} className="cancel-btn">
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default CreateTask;
