import React, { useState, useEffect } from 'react';
import { useParams, useLocation, useNavigate } from 'react-router-dom';
import Sidebar from '../components/shared/SideBar/Sidebar';
import TeamHeader from '../components/shared/TeamHeader/TeamHeader.jsx';
import { fetchProjectById, fetchTasksBySprintId } from '../services/groupService';
import '../assets/styles/global.css';
import './css/Project.css';

const Project = () => {
  const { userId, projectId, sprintId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const [projectData, setProjectData] = useState(null);
  const [error, setError] = useState(null);
  const [selectedSprint, setSelectedSprint] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [activeTab, setActiveTab] = useState('Team task');

  useEffect(() => {
    const determineActiveTab = () => {
      const path = location.pathname;
      if (path.includes('sprints')) return 'Team task';
      if (path.includes('dashboard')) return 'Dashboard';
      if (path.includes('introduce')) return 'Introduce';
      if (path.includes('my-task')) return 'My task';
      if (path.includes('roadmap')) return 'Roadmap';
      if (path.includes('rate')) return 'Rate';
      return 'Team task';
    };
    setActiveTab(determineActiveTab());
  }, [location.pathname]);

  useEffect(() => {
    const loadProject = async () => {
      try {
        console.log(`ƒêang l·∫•y d·ªØ li·ªáu d·ª± √°n v·ªõi projectId: ${projectId}`);
        const data = await fetchProjectById(projectId);
        console.log('D·ªØ li·ªáu d·ª± √°n:', data);
        if (!data || !data.project_id) {
          throw new Error('D·ªØ li·ªáu d·ª± √°n kh√¥ng h·ª£p l·ªá ho·∫∑c kh√¥ng t√¨m th·∫•y');
        }
        setProjectData(data);
        if (data.sprints && data.sprints.length > 0) {
          const sprint = sprintId 
            ? data.sprints.find(s => s.sprint_id === parseInt(sprintId)) 
            : data.sprints[data.sprints.length - 1];
          setSelectedSprint(sprint || data.sprints[data.sprints.length - 1]);
        } else {
          console.log('Kh√¥ng c√≥ sprint n√†o ƒë∆∞·ª£c t√¨m th·∫•y');
          setError('D·ª± √°n n√†y kh√¥ng c√≥ sprint n√†o');
        }
      } catch (err) {
        console.error('L·ªói khi t·∫£i d·ª± √°n:', err);
        setError(`Kh√¥ng th·ªÉ t·∫£i th√¥ng tin d·ª± √°n. Vui l√≤ng ki·ªÉm tra k·∫øt n·ªëi server ho·∫∑c th·ª≠ l·∫°i sau. Chi ti·∫øt l·ªói: ${err.message}`);
      }
    };
    loadProject();
  }, [projectId, sprintId]);

  useEffect(() => {
    const loadTasks = async () => {
      if (selectedSprint) {
        try {
          console.log(`ƒêang l·∫•y task cho sprintId: ${selectedSprint.sprint_id}`);
          const taskData = await fetchTasksBySprintId(selectedSprint.sprint_id);
          console.log('D·ªØ li·ªáu task:', taskData);
          setTasks(taskData);
        } catch (err) {
          console.error('Kh√¥ng th·ªÉ t·∫£i danh s√°ch task:', err);
          setTasks([]);
          setError(`Kh√¥ng th·ªÉ t·∫£i danh s√°ch task: ${err.message}`);
        }
      }
    };
    loadTasks();
  }, [selectedSprint]);

  const handleSprintChange = (sprint) => {
    setSelectedSprint(sprint);
    navigate(`/groups/${userId}/projects/${projectId}/sprints/${sprint.sprint_id}`);
  };

  const handleTabChange = (tabName) => {
    setActiveTab(tabName);
    const tabPath = tabName.toLowerCase().replace(' ', '-');
    if (tabName === 'Team task' && selectedSprint) {
      navigate(`/groups/${userId}/projects/${projectId}/sprints/${selectedSprint.sprint_id}`);
    } else {
      navigate(`/groups/${userId}/projects/${projectId}/${tabPath}`);
    }
  };

  const toDoTasks = tasks.filter(task => task.status === 'To-Do');
  const inProgressTasks = tasks.filter(task => task.status === 'In-Progress');
  const completedTasks = tasks.filter(task => task.status === 'Completed');

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  if (!projectData) {
    return <div className="loading-message">ƒêang t·∫£i d·ªØ li·ªáu d·ª± √°n...</div>;
  }

  return (
    <div className="project-page">
      <Sidebar />
      <div className="content-container">
        <TeamHeader
          teamName={projectData.group_name || 'Name team'}
          members={projectData.members || []}
          activeTab={activeTab}
          sprints={projectData.sprints || []}
          onSprintChange={handleSprintChange}
          onTabChange={handleTabChange}
        />
        <div className="project-content">
          {activeTab === 'Team task' && selectedSprint && (
            <div className="task-board">
              <div className="task-column">
                <h3 className="column-header">
                  To-Do ({toDoTasks.length})
                  <span className="column-indicator gray"></span>
                </h3>
                {toDoTasks.length > 0 ? (
                  toDoTasks.map(task => (
                    <div key={task.task_id} className="task-card">
                      <div className="task-title">{task.title}</div>
                      <div className="task-date">
                        {new Date(task.due_date).toLocaleDateString()} {new Date(task.due_date).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                      </div>
                      <div className="task-labels">
                        <span className="label sql">SQL</span>
                        <span className="label backend">Backend</span>
                      </div>
                      <div className="task-actions">
                        <span className="action-icon">üìã 5</span>
                        <span className="action-icon">üí¨ 3</span>
                        <span className="action-icon">üîó 3</span>
                      </div>
                    </div>
                  ))
                ) : (
                  <p>Kh√¥ng c√≥ task n√†o.</p>
                )}
              </div>
              <div className="task-column">
                <h3 className="column-header">
                  In-progress ({inProgressTasks.length})
                  <span className="column-indicator yellow"></span>
                </h3>
                {inProgressTasks.length > 0 ? (
                  inProgressTasks.map(task => (
                    <div key={task.task_id} className="task-card">
                      <div className="task-title">{task.title}</div>
                      <div className="task-date">
                        {new Date(task.due_date).toLocaleDateString()} {new Date(task.due_date).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                      </div>
                      <div className="task-labels">
                        <span className="label sql">SQL</span>
                        <span className="label backend">Backend</span>
                      </div>
                      <div className="task-actions">
                        <span className="action-icon">üìã 5</span>
                        <span className="action-icon">üí¨ 3</span>
                        <span className="action-icon">üîó 3</span>
                      </div>
                    </div>
                  ))
                ) : (
                  <p>Kh√¥ng c√≥ task n√†o.</p>
                )}
              </div>
              <div className="task-column">
                <h3 className="column-header">
                  Completed ({completedTasks.length})
                  <span className="column-indicator green"></span>
                </h3>
                {completedTasks.length > 0 ? (
                  completedTasks.map(task => (
                    <div key={task.task_id} className="task-card completed">
                      <div className="task-title">{task.title}</div>
                      <div className="task-date">
                        Completed: {new Date(task.due_date).toLocaleDateString()} {new Date(task.due_date).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                      </div>
                      <div className="task-labels">
                        <span className="label sql">SQL</span>
                        <span className="label backend">Backend</span>
                      </div>
                      <div className="task-actions">
                        <span className="action-icon">üìã 3</span>
                        <span className="action-icon">üí¨ 3</span>
                        <span className="action-icon">üîó 3</span>
                      </div>
                    </div>
                  ))
                ) : (
                  <p>Kh√¥ng c√≥ task n√†o.</p>
                )}
              </div>
            </div>
          )}
          {activeTab === 'Introduce' && (
            <div>
              <h3>Introduce</h3>
              <p>Project: {projectData.project_name}</p>
              <p>Group: {projectData.group_name}</p>
              <p>Members: {projectData.members?.length || 0}</p>
            </div>
          )}
          {activeTab === 'Dashboard' && (
            <div>
              <h3>Dashboard</h3>
              <p>Dashboard content will be displayed here.</p>
            </div>
          )}
          {activeTab === 'My task' && (
            <div>
              <h3>My Tasks</h3>
              <p>My tasks content will be displayed here.</p>
            </div>
          )}
          {activeTab === 'Roadmap' && (
            <div>
              <h3>Roadmap</h3>
              <p>Roadmap content will be displayed here.</p>
            </div>
          )}
          {activeTab === 'Rate' && (
            <div>
              <h3>Rate</h3>
              <p>Rate content will be displayed here.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Project;
