import React from 'react';
import './ClassCard.css';

// Dữ liệu giả lập cho danh sách thành viên
const teammates = [
  { id: 1, src: 'teammate0.png' },
  { id: 2, src: 'teammate1.png' },
  { id: 3, src: 'teammate2.png' },
  { id: 4, src: 'teammate3.png' },
  { id: 5, src: 'teammate4.png' },
];

const ClassCard = () => {
  return (
    <div className="classcard-container">
      {/* Avatar và số nhóm */}
      <div className="classcard-avatar">
        <div className="classcard-avatar-bg"></div>
        <div className="classcard-avatar-number">1</div>
      </div>

      {/* Tên lớp và mô tả */}
      <div className="classcard-name-description">
        <div className="classcard-name">
          <div className="classcard-title">Class 01</div>
        </div>
        <div className="classcard-description">
          <div className="classcard-description-text">Group 1: Tên dự án</div>
        </div>
      </div>

      {/* Danh sách thành viên */}
      <div className="classcard-members">
        <div className="classcard-members-list">
          {teammates.map((teammate) => (
            <div key={teammate.id} className="classcard-member">
              <img className="classcard-member-img" src={teammate.src} alt={`Teammate ${teammate.id}`} />
            </div>
          ))}
        </div>
        <div className="classcard-members-count">12 members</div>
      </div>

      {/* Icon More */}
      <img className="classcard-more-icon" src="ri-more-fill0.svg" alt="More options" />
    </div>
  );
};

export default ClassCard;
