import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './TeamHeader.css';

const TeamHeader = ({ userId, onTeamChange }) => {
  const [teams, setTeams] = useState([]); // Danh sách nhóm từ backend
  const [selectedTeam, setSelectedTeam] = useState(null); // Nhóm hiện tại
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Lấy danh sách nhóm từ backend khi component được mount
  useEffect(() => {
    const fetchTeams = async () => {
      setLoading(true);
      try {
        const response = await axios.get(`/api/users/${userId}/teams`);
        setTeams(response.data);
        // Chọn nhóm đầu tiên làm mặc định nếu có
        if (response.data.length > 0) {
          setSelectedTeam(response.data[0]);
          onTeamChange(response.data[0].teamId); // Gọi callback để cập nhật nhóm
        }
      } catch (err) {
        setError('Không thể tải danh sách nhóm. Vui lòng thử lại sau.');
        console.error('Error fetching teams:', err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchTeams();
  }, [userId, onTeamChange]);

  // Xử lý khi người dùng chọn nhóm
  const handleTeamChange = (event) => {
    const teamId = event.target.value;
    const team = teams.find((t) => t.teamId === teamId);
    setSelectedTeam(team);
    onTeamChange(teamId); // Gọi callback để cập nhật nhóm
  };

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="team-header">
      <div className="team-header-left">
        <h1 className="team-header-title">Teams</h1>
        {loading ? (
          <p>Đang tải...</p>
        ) : teams.length > 0 ? (
          <div className="team-selector">
            <label htmlFor="team-select">Change Team: </label>
            <select
              id="team-select"
              value={selectedTeam?.teamId || ''}
              onChange={handleTeamChange}
            >
              {teams.map((team) => (
                <option key={team.teamId} value={team.teamId}>
                  {team.teamName}
                </option>
              ))}
            </select>
          </div>
        ) : (
          <p>Không có nhóm nào để hiển thị.</p>
        )}
      </div>
      <div className="team-header-right">
        <div className="search-bar">
          <input type="text" placeholder="Search for teams..." />
        </div>
        <button className="join-team-btn">Join Team</button>
      </div>
    </div>
  );
};

export default TeamHeader;
