import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Sidebar from '../components/shared/SideBar/Sidebar';
import TeamHeader from '../components/shared/TeamHeader/TeamHeader';
import { fetchProjectById, fetchTasksBySprintId } from '../services/groupService';
import './css/Project.css';

const Project = () => {
  const { userId, classId, projectId, sprintId, tab } = useParams();
  const navigate = useNavigate();
  const [projectData, setProjectData] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const loadData = async () => {
      setLoading(true);
      try {
        if (!projectId || isNaN(projectId)) {
          throw new Error('projectId không hợp lệ');
        }
        const project = await fetchProjectById(projectId);
        if (!project || !project.project_id) {
          throw new Error('Không tìm thấy dự án');
        }
        setProjectData(project);

        if (sprintId) {
          const taskData = await fetchTasksBySprintId(sprintId);
          setTasks(taskData);
        }
      } catch (err) {
        setError('Không thể tải dữ liệu. Vui lòng thử lại sau.');
        console.error('Error in loadData:', err.message);
      } finally {
        setLoading(false);
      }
    };
    loadData();
  }, [projectId, sprintId]);

  const handleSprintChange = (sprint) => {
    navigate(`/userID/${userId}/classes/${classId}/projects/${projectId}/sprints/${sprint.sprint_id}`);
  };

  const handleTabChange = (tabName) => {
    const tabPath = tabName === 'Team task' ? `sprints/${projectData?.sprints[projectData.sprints.length - 1]?.sprint_id || ''}` : tabName.toLowerCase().replace(' ', '-');
    navigate(`/userID/${userId}/classes/${classId}/projects/${projectId}/${tabPath}`);
  };

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  if (loading || !projectData) {
    return <div className="loading">Đang tải dữ liệu...</div>;
  }

  return (
    <div className="project-page">
      <Sidebar />
      <div className="content-container">
        <TeamHeader
          className={projectData.class_name}
          classCode={projectData.class_code}
          teamName={projectData.group_name}
          projectName={projectData.project_name}
          members={projectData.members || []}
          activeTab={tab || (sprintId ? 'Team task' : 'Introduce')}
          onTabChange={handleTabChange}
        />
        <div className="project-content">
          {sprintId ? (
            <>
              <h3>Tasks</h3>
              {tasks.length > 0 ? (
                <div className="task-list">
                  {tasks.map((task, index) => (
                    <div key={index} className="task-item">
                      <h4>{task.title}</h4>
                      <p>{task.description || 'Không có mô tả'}</p>
                      <p>Trạng thái: {task.status}</p>
                      <p>Hạn chót: {task.due_date ? new Date(task.due_date).toLocaleDateString() : 'Chưa đặt'}</p>
                      <p>Tiến độ: {task.progress_percentage}%</p>
                      <p>Người thực hiện: {task.assigned_to_username || 'Chưa giao'}</p>
                    </div>
                  ))}
                </div>
              ) : (
                <p>Không có nhiệm vụ nào để hiển thị.</p>
              )}
            </>
          ) : (
            <>
              <h3>Introduce</h3>
              <div className="project-details">
                <p><strong>Tên dự án:</strong> {projectData.project_name}</p>
                <p><strong>Nhóm:</strong> {projectData.group_name}</p>
                <p><strong>Lớp:</strong> {projectData.class_name}</p>
                <p><strong>Mô tả:</strong> {projectData.description || 'Chưa có mô tả'}</p>
                <p><strong>Công cụ sử dụng:</strong> {projectData.tools_used || 'Chưa xác định'}</p>
                <p><strong>Trạng thái:</strong> {projectData.status}</p>
                <p>
                  <strong>Kho GitHub:</strong>{' '}
                  {projectData.github_repo_url ? (
                    <a href={projectData.github_repo_url} target="_blank" rel="noopener noreferrer">
                      {projectData.github_repo_url}
                    </a>
                  ) : (
                    'Chưa có'
                  )}
                </p>
                <p><strong>Số thành viên:</strong> {projectData.members.length}</p>
                <div className="members-list">
                  <h4>Thành viên:</h4>
                  {projectData.members.map((member, index) => (
                    <p key={index}>
                      {member.username} (ID: {member.user_id})
                    </p>
                  ))}
                </div>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Project;
