// frontend/src/pages/GroupPage.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import ClassCard from '../components/shared/ClassCard/ClassCard';
import '../assets/styles/global.css'; // Import global.css
import "./css/GroupPage.css"

const fetchGroupsByUserId = async (userId) => {
  try {
    const response = await fetch(`http://localhost:5000/api/groups?user_id=${userId}`);
    if (!response.ok) {
      throw new Error('Failed to fetch groups');
    }
    return await response.json();
  } catch (error) {
    throw error;
  }
};

const GroupPage = () => {
  const { id } = useParams(); // Lấy userId từ URL
  const [groups, setGroups] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadGroups = async () => {
      try {
        const data = await fetchGroupsByUserId(id);
        setGroups(data);
      } catch (err) {
        setError('Failed to load groups');
      }
    };
    loadGroups();
  }, [id]);

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="container">
      {/* Header với tiêu đề, thanh tìm kiếm, và nút Join Class */}
      <div className="page-header">
        <h1 className="page-title">Class</h1>
        <div className="search-bar">
          <input type="text" placeholder="Search..." />
        </div>
        <button className="join-class-btn">Join Class</button>
      </div>

      {/* Danh sách ClassCard */}
      <div className="card-container">
        {groups.length > 0 ? (
          groups.map((group, index) => (
            <ClassCard
              key={index}
              className={group.className || `Class 0${index + 1}`}
              groupName={group.groupName || `Group ${index + 1}`}
              projectName="Tên dự án" // Placeholder, thay bằng dữ liệu thực tế nếu có
              memberCount={group.members?.length || 12}
              avatarNumber={index + 1}
              avatarColor={group.avatarColor || '#6AD2FF'} // Placeholder màu, thay bằng dữ liệu thực tế nếu có
              members={group.members || []}
            />
          ))
        ) : (
          <p>No groups found.</p>
        )}
      </div>
    </div>
  );
};

export default GroupPage;
