import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Sidebar from '../../components/shared/SideBar/Sidebar';
import ClassCard from '../../components/shared/ClassCard/ClassCard';
import { fetchGroupsByUserId, fetchProjectByClassId } from '../../services/groupService';
import '../../assets/styles/global.css';
import './css/GroupPage.css';

const GroupPage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const [groups, setGroups] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadGroups = async () => {
      try {
        const data = await fetchGroupsByUserId(userId);
        console.log('Dữ liệu nhóm:', data);
        setGroups(data);
      } catch (err) {
        console.error('Lỗi khi tải danh sách nhóm:', err);
        setError('Không thể tải danh sách nhóm. Vui lòng thử lại sau.');
      }
    };
    loadGroups();
  }, [userId]);

  const handleClassCardClick = async (classId) => {
    try {
      const project = await fetchProjectByClassId(classId, userId);
      if (project && project.project_id) {
        const lastSprint = project.sprints && project.sprints.length > 0 
          ? project.sprints[project.sprints.length - 1] 
          : null;
        if (lastSprint) {
          navigate(`/groups/${userId}/projects/${project.project_id}/sprints/${lastSprint.sprint_id}`);
        } else {
          console.error('Dự án không có sprint nào');
          navigate(`/groups/${userId}/projects/${project.project_id}/introduce`);
        }
      } else {
        console.error('Không tìm thấy dự án cho lớp học này');
      }
    } catch (error) {
      console.error('Lỗi khi lấy projectId:', error);
    }
  };

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="group-page">
      <Sidebar />
      <div className="content-container">
        <div className="header">
          <h1>Danh sách nhóm của bạn</h1>
        </div>
        <div className="class-card-container">
          {groups.length > 0 ? (
            groups.map((group, index) => (
              <ClassCard
                key={index}
                classId={group.classId}
                className={group.className}
                groupName={group.groupName}
                projectName={group.projectName}
                memberCount={group.memberCount}
                avatarNumber={group.avatarNumber}
                avatarColor={group.avatarColor}
                members={group.members}
                onClick={() => handleClassCardClick(group.classId)}
              />
            ))
          ) : (
            <p>Không có nhóm nào để hiển thị.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default GroupPage;
