import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Sidebar from '../components/shared/SideBar/Sidebar';
import TeamHeader from '../components/TeamHeader';
import '../assets/styles/global.css';
import './css/Group.css';

const Group = () => {
  const { userId, classId } = useParams(); // Lấy userId và classId từ URL
  const [group, setGroup] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchGroupData = async () => {
      try {
        const response = await fetch(`/api/groups/${classId}`);
        if (!response.ok) {
          throw new Error('Không thể tải thông tin nhóm');
        }
        const data = await response.json();
        setGroup(data);
      } catch (err) {
        setError(err.message);
      }
    };

    fetchGroupData();
  }, [classId]);

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  if (!group) {
    return <div className="loading-message">Đang tải...</div>;
  }

  return (
    <div className="group-details-page">
      <Sidebar />
      <div className="content-container">
        <TeamHeader />
        <div className="group-details-content">
          <h2 className="section-title">Thông tin nhóm</h2>
          <div className="group-info">
            <p><strong>Tên nhóm:</strong> {group.group_name}</p>
            <p><strong>Thành viên:</strong></p>
            <ul className="member-list">
              {group.members.map((member, index) => (
                <li key={index} className="member-item">
                  <img
                    src={member.avatar || '/default-avatar.png'}
                    alt={member.username}
                    className="member-avatar"
                  />
                  <span>{member.username}</span>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Group;
