import React from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import './ClassCard.css';

const ClassCard = ({
  classId,
  className,
  groupName,
  projectName,
  memberCount,
  avatarNumber,
  avatarColor,
  members,
}) => {
  const navigate = useNavigate();
  const { userId } = useParams(); // Lấy userId từ URL

  const handleCardClick = () => {
    navigate(`/groups/${userId}/class/${classId}`); // Điều hướng đến trang chi tiết nhóm
  };

  return (
    <div className="class-card" onClick={handleCardClick}>
      <div className="class-card-header">
        <div
          className="class-avatar"
          style={{ backgroundColor: avatarColor }}
        >
          {avatarNumber}
        </div>
        <h3 className="class-name">{className}</h3>
      </div>
      <div className="class-card-body">
        <p><strong>Nhóm:</strong> {groupName}</p>
        <p><strong>Dự án:</strong> {projectName}</p>
        <p><strong>Số thành viên:</strong> {memberCount}</p>
        <div className="members-avatars">
          {members.slice(0, 3).map((member, index) => (
            <img
              key={index}
              src={member.avatar || '/default-avatar.png'}
              alt={member.username}
              className="member-avatar"
            />
          ))}
          {memberCount > 3 && <span className="more-members">+{memberCount - 3}</span>}
        </div>
      </div>
    </div>
  );
};

export default ClassCard;
