import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Sidebar from '../components/shared/SideBar/Sidebar';
import { fetchUserProfile, updateUserProfile, changePassword } from '../services/profileService';
import avatarMap from '../utils/avatarMap';
import './css/Profile.css';

const Profile = () => {
  const { userId } = useParams();
  const [profile, setProfile] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editedProfile, setEditedProfile] = useState({});
  const [showPasswordModal, setShowPasswordModal] = useState(false);
  const [passwordData, setPasswordData] = useState({ password: '', newPassword: '', confirmPassword: '' });
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const data = await fetchUserProfile(userId);
        setProfile(data);
        setEditedProfile(data);
      } catch (err) {
        setError(`Không thể tải thông tin cá nhân: ${err.message}. Vui lòng kiểm tra backend và database.`);
        console.error('Error fetching profile:', err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [userId]);

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleCancel = () => {
    setEditedProfile(profile);
    setIsEditing(false);
  };

  const handleSave = async () => {
    setLoading(true);
    try {
      await updateUserProfile(userId, editedProfile);
      setProfile({ ...editedProfile });
      setIsEditing(false);
      alert('Thông tin đã được cập nhật thành công!');
    } catch (err) {
      setError('Lỗi khi cập nhật thông tin: ' + err.message);
      console.error('Error updating profile:', err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditedProfile((prev) => ({ ...prev, [name]: value }));
  };

  const handlePasswordChange = (e) => {
    const { name, value } = e.target;
    setPasswordData((prev) => ({ ...prev, [name]: value }));
  };

  const handlePasswordSubmit = async () => {
    if (passwordData.newPassword !== passwordData.confirmPassword) {
      setError('Mật khẩu mới và xác nhận mật khẩu không khớp.');
      return;
    }
    setLoading(true);
    try {
      await changePassword(userId, passwordData);
      setShowPasswordModal(false);
      setPasswordData({ password: '', newPassword: '', confirmPassword: '' });
      alert('Mật khẩu đã được đổi thành công!');
    } catch (err) {
      setError('Lỗi khi đổi mật khẩu: ' + err.message);
      console.error('Error changing password:', err.message);
    } finally {
      setLoading(false);
    }
  };

  if (error) return <div className="error-message">{error}</div>;
  if (loading || !profile) return <div className="loading">Đang tải...</div>;

  const avatarSrc = profile.avatar && avatarMap[profile.avatar] ? avatarMap[profile.avatar] : '/assets/images/default-avatar.jpg';

  return (
    <div className="profile-page">
      <Sidebar />
      <div className="content-container">
        <div className="profile-content">
          <div className="profile-header">
            <img
              src={avatarSrc}
              alt={profile.username || 'User'}
              className="profile-avatar"
            />
            <div className="profile-info">
              <h2>{profile.username}</h2>
              <p>{profile.email}</p>
            </div>
          </div>
          <div className="profile-details">
            <div className="detail-section left-column">
              <div>
                <label>Username</label>
                <p>{profile.username || 'Not provided'}</p>
              </div>
              <div>
                <label>Email</label>
                <p>{profile.email || 'Not provided'}</p>
              </div>
            </div>
            <div className="detail-section right-column">
              <label>Avatar</label>
              {isEditing ? (
                <div>
                  <input
                    type="file"
                    name="avatar"
                    onChange={(e) => {
                      const file = e.target.files[0];
                      setEditedProfile((prev) => ({ ...prev, avatar: file ? file.name : prev.avatar }));
                    }}
                    className="edit-input"
                  />
                </div>
              ) : (
                <img
                  src={avatarSrc}
                  alt="Avatar"
                  className="avatar-preview"
                />
              )}
            </div>
          </div>
          <div className="profile-actions">
            {isEditing ? (
              <>
                <button className="cancel-button" onClick={handleCancel}>Cancel</button>
                <button className="done-button" onClick={handleSave}>Done</button>
              </>
            ) : (
              <>
                <button className="edit-button" onClick={handleEdit}>Edit</button>
                <button className="change-password-button" onClick={() => setShowPasswordModal(true)}>Change Password</button>
              </>
            )}
          </div>
        </div>
        {showPasswordModal && (
          <div className="modal">
            <div className="modal-content">
              <h3>Change your password</h3>
              <div className="modal-section">
                <label>Password</label>
                <input
                  type="password"
                  name="password"
                  value={passwordData.password}
                  onChange={handlePasswordChange}
                  placeholder="Enter current password"
                />
              </div>
              <div className="modal-section">
                <label>New Password</label>
                <input
                  type="password"
                  name="newPassword"
                  value={passwordData.newPassword}
                  onChange={handlePasswordChange}
                  placeholder="Enter new password"
                />
              </div>
              <div className="modal-section">
                <label>Confirm</label>
                <input
                  type="password"
                  name="confirmPassword"
                  value={passwordData.confirmPassword}
                  onChange={handlePasswordChange}
                  placeholder="Confirm new password"
                />
              </div>
              <div className="modal-actions">
                <button className="set-password-button" onClick={handlePasswordSubmit}>Set Password</button>
                <button className="cancel-modal-button" onClick={() => setShowPasswordModal(false)}>Cancel</button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Profile;
