/* frontend/src/pages/Project.js */
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import TeamHeader from '../components/shared/TeamHeader/TeamHeader';
import './css/Project.css';

const fetchProjectById = async (projectId) => {
  try {
    const response = await fetch(`http://localhost:5000/api/projects/${projectId}`);
    if (!response.ok) {
      throw new Error('Failed to fetch project');
    }
    return await response.json();
  } catch (error) {
    throw error;
  }
};

const Project = () => {
  const { projectId } = useParams();
  const [projectData, setProjectData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadProject = async () => {
      try {
        const data = await fetchProjectById(projectId);
        setProjectData(data);
      } catch (err) {
        setError('Failed to load project');
      }
    };
    loadProject();
  }, [projectId]);

  if (error) {
    return <div>{error}</div>;
  }

  if (!projectData) {
    return <div>Loading...</div>;
  }

  return (
    <div className="project-page">
      <TeamHeader
        teamName={projectData.group_name || 'Name team'}
        members={projectData.members || []}
        activeTab="Introduce"
        sprints={projectData.sprints || []} // Truyền danh sách sprint vào TeamHeader
      />
      <div className="project-content">
        <p>Project: {projectData.project_name}</p>
        <p>Group: {projectData.group_name}</p>
        <p>Members: {projectData.members?.length || 0}</p>
        {/* Nội dung khác của trang Project có thể thêm sau */}
      </div>
    </div>
  );
};

export default Project;
