import React from 'react';
import './TeamHeader.css';

const TeamHeader = ({ teamName, members, activeTab, sprints, onSprintChange, onTabChange }) => {
  const tabs = ['Introduce', 'Team task', 'My task', 'Dashboard', 'Roadmap', 'Rate'];

  const handleSprintChange = (e) => {
    const selectedSprintId = parseInt(e.target.value);
    const sprint = sprints.find(s => s.sprint_id === selectedSprintId);
    if (sprint) {
      onSprintChange(sprint);
    }
  };

  return (
    <div className="team-header">
      <div className="team-info">
        <h2>{teamName}</h2>
        <div className="team-members">
          {members.map((member, index) => (
            <img
              key={index}
              src={`/avatars/${member.avatar}`}
              alt={`Member ${member.user_id}`}
              className="member-avatar"
            />
          ))}
          <span className="member-count">+{members.length}</span>
        </div>
      </div>
      <div className="team-tabs">
        {tabs.map(tab => (
          <button
            key={tab}
            className={`tab-button ${activeTab === tab ? 'active' : ''}`}
            onClick={() => onTabChange(tab)}
          >
            {tab}
          </button>
        ))}
      </div>
      {activeTab === 'Team task' && sprints.length > 0 && (
        <div className="sprint-selector">
          <select onChange={handleSprintChange} value={sprints[sprints.length - 1]?.sprint_id}>
            {sprints.map(sprint => (
              <option key={sprint.sprint_id} value={sprint.sprint_id}>
                {sprint.sprint_name}
              </option>
            ))}
          </select>
        </div>
      )}
    </div>
  );
};

export default TeamHeader;
